with list_temp as (
select #{item.group_id,jdbcType=INTEGER} as group_id, #{item.hos_id,jdbcType=INTEGER} as hos_id, #{item.copy_code,jdbcType=VARCHAR} as copy_code, #{item.year,jdbcType=VARCHAR} as year, #{item.month,jdbcType=VARCHAR} as month, #{item.store_id,jdbcType=INTEGER} as store_id, #{item.inv_id,jdbcType=INTEGER} as inv_id, #{item.location_id,jdbcType=INTEGER} as location_id from dual
) select a.group_id, a.hos_id, a.copy_code, a.year, a.month, a.store_id, a.inv_id, nvl(b.end_amount, 0) as end_amount, nvl(b.end_money, 0) as end_money, nvl(b.end_sale_money, 0) as end_sale_money, nvl(b.begin_amount, 0) as begin_amount, nvl(b.begin_money, 0) as begin_money, nvl(b.begin_sale_money, 0) as begin_sale_money, nvl(b.in_amount, 0) as in_amount, nvl(b.in_money, 0) as in_money, nvl(b.in_sale_money, 0) as in_sale_money, nvl(b.out_amount, 0) as out_amount, nvl(b.out_money, 0) as out_money, nvl(b.out_sale_money, 0) as out_sale_money, nvl(b.y_in_amount, 0) as y_in_amount, nvl(b.y_in_money, 0) as y_in_money, nvl(b.y_in_sale_money, 0) as y_in_sale_money, nvl(b.y_out_amount, 0) as y_out_amount, nvl(b.y_out_money, 0) as y_out_money, nvl(b.y_out_sale_money, 0) as y_out_sale_money, nvl(b.sale_zero_error, 0) as sale_zero_error, nvl(b.location_id, 0) as location_id from list_temp a inner join mat_inv_balance b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.copy_code = b.copy_code and a.year = b.year and a.month = b.month and a.store_id = b.store_id and a.inv_id = b.inv_id and nvl(a.location_id, 0) = nvl(b.location_id, 0) order by a.year, a.month, a.store_id, a.inv_id
