mapper.insert:
INSERT INTO MAT_INV_PICTURE ( group_id , hos_id , copy_code , inv_id , picture_name , picture_url ) select t.* from(
select #{item.group_id,jdbcType=INTEGER} , #{item.hos_id,jdbcType=INTEGER} , #{item.copy_code,jdbcType=VARCHAR} , #{item.inv_id,jdbcType=INTEGER} , #{item.picture_name,jdbcType=VARCHAR} , #{item.picture_url,jdbcType=VARCHAR} from dual
)t

mapper.update:

UPDATE MAT_INV_PICTURE 


picture_name = #{picture_name,jdbcType=VARCHAR}
,


picture_url = #{picture_url,jdbcType=VARCHAR}
,




AND group_id = #{group_id,jdbcType=INTEGER}


AND hos_id = #{hos_id,jdbcType=INTEGER}


AND copy_code = #{copy_code,jdbcType=VARCHAR}


AND inv_id = #{inv_id,jdbcType=INTEGER}


AND picture_name = #{picture_name,jdbcType=VARCHAR}

update.foreach:
UPDATE MAT_INV_PICTURE


		picture_name = #{item.picture_name,jdbcType=VARCHAR},


		picture_url = #{item.picture_url,jdbcType=VARCHAR},




		AND group_id = #{item.group_id,jdbcType=INTEGER}


		AND hos_id = #{item.hos_id,jdbcType=INTEGER}


		AND copy_code = #{item.copy_code,jdbcType=VARCHAR}


		AND inv_id = #{item.inv_id,jdbcType=INTEGER}

