mapper.select:
select hf.group_id, hf.hos_id, hf.fac_id, hf.fac_code, hf.fac_name, nvl(mfc.cert_num, 0) cert_num, nvl(mif.inv_num, 0) inv_num from hos_fac_dict hf left join (select group_id, hos_id, copy_code, fac_id, nvl(count(1), 0) cert_num from mat_fac_cert
and group_id = #{group_id, jdbcType=INTEGER}
and hos_id = #{hos_id, jdbcType=INTEGER}
and copy_code = #{copy_code, jdbcType=VARCHAR}
group by group_id, hos_id, copy_code, fac_id) mfc on hf.group_id = mfc.group_id and hf.hos_id = mfc.hos_id and hf.fac_id = mfc.fac_id left join (select group_id, hos_id, copy_code, fac_id, nvl(count(1), 0) inv_num from MAT_INV
and group_id = #{group_id, jdbcType=INTEGER}
and hos_id = #{hos_id, jdbcType=INTEGER}
and copy_code = #{copy_code, jdbcType=VARCHAR}
group by group_id, hos_id, copy_code, fac_id) mif on hf.group_id = mif.group_id and hf.hos_id = mif.hos_id and hf.fac_id = mif.fac_id where is_mat = 1
and hf.group_id = #{group_id, jdbcType=INTEGER}
and hf.hos_id = #{hos_id, jdbcType=INTEGER}
and hf.type_code = #{type_code, jdbcType=VARCHAR}
and hf.is_disable = #{is_disable, jdbcType=INTEGER}
and mfc.cert_num > 0
and (mfc.cert_num <= 0 or mfc.cert_num is null)
and ( hf.fac_code like '%${fac_name}%' or hf.fac_name like '%${fac_name}%' or hf.spell_code like upper('%${fac_name}%') or hf.spell_code like lower('%${fac_name}%') or hf.wbx_code like upper('%${fac_name}%') or hf.wbx_code like lower('%${fac_name}%') )
order by hf.fac_code

select mfc.group_id, mfc.hos_id, mfc.copy_code, mfc.cert_id, mfc.fac_id, mfc.cert_code, mfc.cert_type_code, mct.cert_type_name, mfc.start_date, mfc.end_date, mfc.cert_date, mfc.is_stop, mfc.authent_state, mfc.check_state, mfc.is_long from mat_fac_cert mfc left join mat_cert_type mct on mfc.group_id = mct.group_id and mfc.hos_id = mct.hos_id and mfc.copy_code = mct.copy_code and mfc.cert_type_code = mct.cert_type_code
and mfc.group_id = #{group_id, jdbcType=INTEGER}
and mfc.hos_id = #{hos_id, jdbcType=INTEGER}
and mfc.copy_code = #{copy_code, jdbcType=VARCHAR}
and mfc.fac_id = #{fac_id, jdbcType=INTEGER}
and mfc.is_stop = #{is_stop, jdbcType=INTEGER}
order by mfc.oper_date desc

select mi.group_id, mi.hos_id, mi.copy_code, mi.fac_id, mi.inv_id, mi.inv_code, mi.inv_name, mi.inv_model, mi.unit_code, hu.unit_name, mi.plan_price, mi.use_state from mat_inv mi left join hos_unit hu on mi.group_id = hu.group_id and mi.hos_id = hu.hos_id and mi.unit_code = hu.unit_code
and mi.group_id = #{group_id, jdbcType=INTEGER}
and mi.hos_id = #{hos_id, jdbcType=INTEGER}
and mi.copy_code = #{copy_code, jdbcType=VARCHAR}
and mi.fac_id = #{fac_id, jdbcType=INTEGER}

mapper.delete:
delete from MAT_FAC_CERT_FILE


		and group_id = #{group_id}


		and hos_id = #{hos_id}


		and copy_code = #{copy_code}


		and cert_id in (${cert_ids})

delete from MAT_FAC_CERT


		and group_id = #{group_id}


		and hos_id = #{hos_id}


		and copy_code = #{copy_code}


		and cert_id in (${cert_ids})

mapper.update:
update MAT_FAC_CERT
authent_state = 2, authent_date = #{oper_date,jdbcType=DATE}, authenter = #{oper_name,jdbcType=VARCHAR}
authent_state = 1, authent_date = null, authenter = ''
check_state = 2, check_date = #{oper_date,jdbcType=DATE}, checker = #{oper_name,jdbcType=VARCHAR}
check_state = 1, check_date = null, checker = ''
and group_id = #{group_id}
and hos_id = #{hos_id}
and copy_code = #{copy_code}
and cert_id in (${cert_ids})

mapper.select:
select mct.group_id,
       mct.hos_id,
       mct.copy_code,
       mct.cert_type_code,
       mct.cert_type_name,
       mct.is_cert_busi,
       mct.is_cert_name,
       mct.is_stop
  from mat_cert_type mct


		and mct.group_id = #{group_id, jdbcType=INTEGER}


		and mct.hos_id = #{hos_id, jdbcType=INTEGER}


		and mct.copy_code = #{copy_code, jdbcType=VARCHAR}


		and mct.cert_type_code = #{cert_type_code, jdbcType=VARCHAR}

select mfc.group_id, mfc.hos_id, mfc.copy_code, mfc.cert_id, mfc.fac_id, mfc.cert_code, mfc.cert_type_code, mfc.start_date, mfc.end_date, mfc.cert_date, mfc.is_stop, mfc.authent_state, mfc.check_state, mfc.is_long from mat_fac_cert mfc
and mfc.group_id = #{group_id, jdbcType=INTEGER}
and mfc.hos_id = #{hos_id, jdbcType=INTEGER}
and mfc.copy_code = #{copy_code, jdbcType=VARCHAR}
and mfc.cert_code = #{cert_code, jdbcType=VARCHAR}
order by mfc.oper_date desc

select mfc.group_id, mfc.hos_id, mfc.copy_code, mfc.cert_id, mfc.cert_code, mfc.cert_name, mfc.cert_type_code, mct.cert_type_name, to_char(mfc.start_date, 'yyyy-MM-dd') start_date, to_char(mfc.end_date, 'yyyy-MM-dd') end_date, mfc.is_long, mfc.cert_busi, mfc.check_state, to_char(wm_concat(mfcf.file_path || mfcf.file_name)) file_path from mat_fac_cert mfc left join mat_cert_type mct on mfc.group_id = mct.group_id and mfc.hos_id = mct.hos_id and mfc.copy_code = mct.copy_code and mfc.cert_type_code = mct.cert_type_code left join mat_fac_cert_file mfcf on mfc.group_id = mfcf.group_id and mfc.hos_id = mfcf.hos_id and mfc.copy_code = mfcf.copy_code and mfc.cert_id = mfcf.cert_id
and mfc.group_id = #{group_id, jdbcType=INTEGER}
and mfc.hos_id = #{hos_id, jdbcType=INTEGER}
and mfc.copy_code = #{copy_code, jdbcType=VARCHAR}
and mfc.cert_id = #{cert_id, jdbcType=INTEGER}
group by mfc.group_id, mfc.hos_id, mfc.copy_code, mfc.cert_id, mfc.cert_code, mfc.cert_name, mfc.cert_type_code, mct.cert_type_name, mfc.start_date, mfc.end_date, mfc.is_long, mfc.cert_busi, mfc.check_state

mapper.update:
update MAT_FAC_CERT


		cert_code = #{cert_code, jdbcType=VARCHAR},


		cert_name = #{cert_name, jdbcType=VARCHAR},


		cert_type_code = #{cert_type_code, jdbcType=VARCHAR},


		start_date = #{start_date, jdbcType=DATE},


		end_date = #{end_date, jdbcType=DATE},


		is_long = #{is_long, jdbcType=INTEGER},


		cert_busi = #{cert_busi, jdbcType=VARCHAR},


		cert_date = #{cert_date, jdbcType=VARCHAR},


		oper_date = #{oper_date, jdbcType=DATE},


		oper_name = #{oper_name, jdbcType=VARCHAR},




		and group_id = #{group_id, jdbcType=INTEGER}


		and hos_id = #{hos_id, jdbcType=INTEGER}


		and copy_code = #{copy_code, jdbcType=VARCHAR}


		and cert_id = #{cert_id, jdbcType=INTEGER}


		and fac_id = #{fac_id, jdbcType=INTEGER}
