mapper.insert:
INSERT INTO mat_out_use ( group_id, hos_id, copy_code, use_code, use_name, wbx_code, spell_code, is_stop , note )
select #{item.group_id}, #{item.hos_id}, #{item.copy_code}, #{item.use_code}, #{item.use_name}, #{item.wbx_code}, #{item.spell_code}, #{item.is_stop}, #{item.note} from dual
)t

mapper.update:
		UPDATE mat_out_use 
			 SET use_name = #{use_name} ,
			 spell_code = #{spell_code} ,
			 wbx_code = #{wbx_code} ,
			 is_stop = #{is_stop},
			 note = #{note}
        WHERE  use_code = #{use_code}
		 and  group_id = #{group_id}
		 and  hos_id = #{hos_id}
		 and  copy_code = #{copy_code}

update.foreach:
        UPDATE mat_out_use 
        	 SET use_name = #{item.use_name} ,
			 spell_code = #{item.spell_code} ,
			 wbx_code = #{item.wbx_code} ,
			 is_stop = #{item.is_stop},
			 note = #{item.note}
        WHERE  use_code = #{item.use_code}
			 and  group_id = #{item.group_id}
			 and  hos_id = #{item.hos_id}
			 and  copy_code = #{item.copy_code}

mapper.delete:
DELETE FROM mat_out_use 
WHERE use_code = #{use_code}   
	and  group_id = #{group_id}   
	and  hos_id = #{hos_id}   
	and  copy_code = #{copy_code}

DELETE FROM mat_out_use 
WHERE

	 use_code = #{item.use_code}
	 and  group_id = #{item.group_id}
	 and  hos_id = #{item.hos_id}
	 and  copy_code = #{item.copy_code}

mapper.select:
SELECT mou.use_code, mou.group_id, mou.hos_id, mou.copy_code, mou.use_name, mou.spell_code, mou.wbx_code, mou.is_stop, mou.note FROM mat_out_use mou
AND mou.use_code like '${use_code}%'
AND mou.group_id = #{group_id}
AND mou.hos_id = #{hos_id}
AND mou.copy_code = #{copy_code}
and (mou.use_name like '%'||#{use_name}||'%' or upper(mou.spell_code) like '%'||#{use_name}||'%' or upper(mou.wbx_code) like '%'||#{use_name}||'%' or lower(mou.spell_code) like '%'||#{use_name}||'%' or lower(mou.wbx_code) like '%'||#{use_name}||'%')
AND mou.spell_code = #{spell_code}
AND mou.wbx_code = #{wbx_code}
AND mou.is_stop = 0
order by mou.use_code asc

		SELECT 
            mou.use_code,
            mou.group_id,
            mou.hos_id,
            mou.copy_code,
            mou.use_name,
            mou.spell_code,
            mou.wbx_code,
            mou.is_stop,
            mou.note
        FROM mat_out_use mou
		WHERE  
		(mou.use_code = #{use_code}  
		or 
		mou.use_name = #{use_name} 
		) 

		   and mou.group_id = #{group_id}   
		   and mou.hos_id = #{hos_id}   
		   and mou.copy_code = #{copy_code}
