mapper.select:
SELECT group_id, hos_id, copy_code, type_code, type_name, wbx_code, spell_code, is_stop, note FROM MAT_FILE_TYPE
group_id = #{group_id}
AND hos_id = #{hos_id}
AND copy_code = #{copy_code}
AND type_code = #{type_code}

SELECT group_id, hos_id, copy_code, type_code, type_name, wbx_code, spell_code, is_stop, note FROM MAT_FILE_TYPE
group_id = #{group_id}
AND hos_id = #{hos_id}
AND copy_code = #{copy_code}
AND type_code like #{type_code}||'%'
and (type_name like '%'||#{type_name}||'%' or upper(spell_code) like '%'||#{type_name}||'%' or upper(wbx_code) like '%'||#{type_name}||'%' or lower(spell_code) like '%'||#{type_name}||'%' or lower(wbx_code) like '%'||#{type_name}||'%')

mapper.update:
UPDATE MAT_FILE_TYPE SET
	type_name = #{type_name},
	is_stop = #{is_stop},
	note = #{note}
WHERE
	group_id = #{group_id}
	AND hos_id = #{hos_id}
	AND copy_code = #{copy_code}
	AND type_code = #{type_code}

mapper.delete:
DELETE FROM MAT_FILE_TYPE WHERE


			group_id = #{item.group_id}


			AND hos_id = #{item.hos_id}


			AND copy_code = #{item.copy_code}


			AND type_code = #{item.type_code}
