mapper.select:
select distinct mrm.group_id, mrm.hos_id, mrm.copy_code, mrm.req_id, nvl(pum.pur_id,'') pur_id, mrm.req_code, pum.pur_code, hsd.store_name, to_char(mrm.make_date,'yyyy-MM-dd') make_date, mrm.is_dir, su.user_name maker, mrm.state state, '查看明细' other_inv from mat_require_main mrm left join mat_req_pur_rela b on mrm.req_id = b.req_id and mrm.group_id = b.group_id and mrm.hos_id = b.hos_id and mrm.copy_code = b.copy_code left join mat_pur_main pum ON pum.pur_id = b.pur_id and pum.copy_code = b.copy_code and pum.hos_id = b.hos_id and pum.group_id = b.group_id left join hos_store_dict hsd on mrm.hos_id = hsd.hos_id and mrm.group_id = hsd.group_id and hsd.store_id = mrm.stock_id
and mrm.stock_no = hsd.store_no
and hsd.is_stop = 0
left join sys_user su on su.user_id = mrm.maker and su.copy_code = mrm.copy_code and su.group_id = mrm.group_id and su.hos_id = mrm.hos_id
mrm.hos_id = #{hos_id}
and mrm.group_id = #{group_id}
and mrm.copy_code = #{copy_code}
and mrm.stock_no = #{stock_no}
and mrm.stock_id = #{stock_id}
and hsd.store_code = #{store_code}
and mrm.req_code like '${req_code}%'
and mrm.is_collect = 1 and mrm.state >= 2 and substr(to_char(mrm.make_date,'yyyyMMdd'),0,6) between #{accYearB}||''||#{accMonthB} and #{accYearE}||''||#{accMonthE}
order by mrm.req_id desc

select mrm.req_code,
       hsd.store_name,
       mid.inv_code,
       mid.inv_name,
       mid.inv_model,
       hu.unit_name,
       nvl(aa.req_amount,0) amount,
       mid.plan_price price,
       nvl(aa.req_amount,0)*nvl(mid.plan_price,0) money
from mat_require_main mrm 
left join hos_store_dict hsd on mrm.hos_id = hsd.hos_id and mrm.group_id = hsd.group_id and mrm.stock_no = hsd.store_no 
     and hsd.store_id = mrm.stock_id and hsd.is_stop = 0
left join (
     select group_id,hos_id,copy_code,total_id,inv_id,sum(nvl(req_amount,0)) req_amount
     from mat_require_rela
     where group_id = #{group_id} 
     	and hos_id = #{hos_id} 
     	and copy_code = #{copy_code}  
     	and total_id = #{req_id}
     group by group_id,hos_id,copy_code,total_id,inv_id
) aa on aa.group_id = mrm.group_id and aa.hos_id = mrm.hos_id and aa.copy_code = mrm.copy_code and mrm.req_id = aa.total_id
left join mat_inv_dict mid on mid.inv_id = aa.inv_id and mid.copy_code = aa.copy_code and mid.hos_id = aa.hos_id
     and mid.group_id = aa.group_id  and mid.is_stop = 0
left join hos_unit hu on hu.group_id = mid.group_id and hu.hos_id = mid.hos_id and hu.unit_code = mid.unit_code
     and hu.is_stop = 0
where mrm.hos_id = #{hos_id}
      and mrm.group_id = #{group_id}
      and mrm.copy_code = #{copy_code}
      and mrm.req_id = #{req_id}
