update.foreach:
UPDATE mat_require_main 
SET  state = 3,
	checker = #{item.user_id,jdbcType=INTEGER},
	check_date = #{item.check_date,jdbcType=DATE}
WHERE	group_id =	#{item.group_id,jdbcType=INTEGER}
	and	hos_id = #{item.hos_id,jdbcType=INTEGER}
	and	copy_code = #{item.copy_code,jdbcType=VARCHAR}
	and	req_id =#{item.req_id,jdbcType=INTEGER}
	and req_code = #{item.req_code,jdbcType=VARCHAR}

UPDATE mat_require_main 
SET  state = 2 ,
	checker = '',
	check_date = ''
WHERE	group_id =	#{item.group_id,jdbcType=INTEGER}
	and	hos_id = #{item.hos_id,jdbcType=INTEGER}
	and	copy_code = #{item.copy_code,jdbcType=VARCHAR}
	and	req_id =#{item.req_id,jdbcType=INTEGER}
	and req_code = #{item.req_code,jdbcType=VARCHAR}

UPDATE mat_require_main 
SET  state = 1,
	 checker = '',
	 check_date = '',
	 return_reason = #{item.return_reason,jdbcType=VARCHAR}
WHERE	group_id =	#{item.group_id,jdbcType=INTEGER}
	and	hos_id = #{item.hos_id,jdbcType=INTEGER}
	and	copy_code = #{item.copy_code,jdbcType=VARCHAR}
	and	req_id = #{item.req_id,jdbcType=INTEGER}

mapper.select:
SELECT a.group_id, a.hos_id, a.copy_code, a.req_id, a.req_code, a.dept_no, a.dept_id, a.stock_no, a.stock_id, hsd.store_code||' '||hsd.store_name store_name, hdd.dept_code||' '||hdd.dept_name dept_names, a.brif, hdd.dept_name, c.user_name maker, a.make_date make_date, su.user_name checker, a.check_date, a.state, a.other_inv, a.return_reason, mdd.field_desc as come_from FROM MAT_REQUIRE_MAIN a left join hos_dept_dict hdd on a.dept_id = hdd.dept_id and a.group_id = hdd.group_id and a.hos_id = hdd.hos_id
and a.dept_no = hdd.dept_no
and hdd.is_stop = 0
left join hos_store_dict hsd on hsd.store_id = a.stock_id and hsd.group_id = a.group_id and hsd.hos_id = a.hos_id
and a.stock_no = hsd.store_no
and hsd.is_stop = 0
left join sys_user c on c.user_id = a.maker and c.hos_id = a.hos_id and c.group_id = a.group_id left join sys_user su on su.user_id = a.checker and su.hos_id = a.hos_id and su.group_id = a.group_id left join mat_data_dict mdd on a.come_from = mdd.field_value and mdd.data_code = '04003'
hdd.dept_id in (select b.perm_code from v_user_data_perm b where b.group_id =#{group_id,jdbcType=INTEGER} and b.hos_id = #{hos_id,jdbcType=INTEGER} and b.user_id = #{user_id,jdbcType=INTEGER} and b.table_code = 'HOS_DEPT_DICT' and b.is_read = 1 and b.is_write = 1 )
a.group_id = #{group_id,jdbcType=INTEGER}
and a.hos_id = #{hos_id,jdbcType=INTEGER}
and a.copy_code = #{copy_code,jdbcType=VARCHAR}
AND a.make_date >= to_date(#{begin_date},'yyyy/MM/dd')
AND a.make_date <= to_date(#{end_date},'yyyy/MM/dd')
AND a.dept_id = #{dept_id,jdbcType=INTEGER}
AND a.dept_no = #{dept_no,jdbcType=INTEGER}
AND a.brif like '%'||#{brif,jdbcType=VARCHAR}||'%'
AND a.stock_no = #{store_no,jdbcType=INTEGER}
AND a.stock_id = #{store_id,jdbcType=INTEGER}
AND state = #{state,jdbcType=INTEGER}
and a.req_code like '${req_code}%'
and a.state > 1
order by a.req_id desc
