mapper.select:
select a.group_id, a.hos_id, a.copy_code, a.apply_id, a.apply_no,hsd.store_name, to_char(a.app_date, 'yyyy-MM-dd hh24:mi:ss') app_date, a.brief, b.dept_name,su.user_name as maker , c.emp_name, a.state, case when a.state = 0 then '已作废' when a.state = 1 then '新建' when a.state = 2 then '已审核' when a.state = 3 then '已发送' when a.state = 4 then '退回' end as state_name, a.back_reason,res.emp_name as response_emp, case when ((nvl(e.rela_amount, 0) = 0) and (nvl(e.pur_amount, 0) = 0)) then 1 when e.rela_amount = e.app_amount then 3 when nvl(e.pur_amount, 0) != 0 then 4 else 2 end as rela_state , case when nvl(e.rela_amount, 0) = 0 and nvl(e.pur_amount, 0) = 0 then '待处理' when e.rela_amount = e.app_amount then '全部处理' when nvl(e.pur_amount, 0) != 0 then '已采购' else '部分处理' end as rela_state_name,sender.user_name as sender from mat_apply_main a left join hos_store_dict hsd on a.group_id = hsd.group_id and a.hos_id = hsd.hos_id and a.store_id = hsd.store_id and hsd.is_stop = 0 left join hos_dept_dict b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.dept_id = b.dept_id
and a.dept_no = b.dept_no
and b.is_stop = 0
left join hos_emp_dict c on a.group_id = c.group_id and a.hos_id = c.hos_id and a.app_emp = c.emp_id and c.is_stop = 0 left join sys_user su on a.group_id= su.group_id and a.hos_id=su.hos_id and a.maker=su.user_id and su.is_stop=0 left join sys_user sender on sender.group_id=a.group_id and sender.hos_id=a.hos_id and sender.user_id=a.sender and sender.is_stop=0 LEFT JOIN hos_emp_dict res ON a.group_id = res.group_id AND a.hos_id = res.hos_id AND a.response_emp = res.emp_id AND res.is_stop = 0 inner join ( select aa.apply_id,sum(nvl(aa.app_amount,0)) app_amount,sum(nvl(bb.rela_amount,0)) as rela_amount, sum(nvl(cc.rela_amount,0)) as pur_amount from ( select mad.group_id,mad.hos_id,mad.copy_code,mad.apply_id,mad.detail_id, sum(nvl(mad.app_amount,0)) app_amount from mat_apply_detail mad
LEFT JOIN mat_inv_dict mid ON mad.group_id = mid.group_id and mad.hos_id = mid.hos_id and mad.copy_code = mid.copy_code and mad.inv_id = mid.inv_id and mad.inv_no = mid.inv_no
mad.group_id = #{group_id,jdbcType=INTEGER} and mad.hos_id = #{hos_id,jdbcType=INTEGER} and mad.copy_code = #{copy_code,jdbcType=VARCHAR} and nvl(mad.is_closed, 0) != 1
AND ( mid.inv_code like '%${inv_code}%' or upper(mid.inv_name) like '%${inv_code}%' or upper(mid.inv_model) like '%${inv_code}%' or upper(mid.spell_code) like '%${inv_code}%' or upper(mid.wbx_code) like '%${inv_code}%' or upper(mid.bid_code) like '%${inv_code}%' or lower(mid.spell_code) like '%${inv_code}%' or lower(mid.bid_code) like '%${inv_code}%' or lower(mid.wbx_code) like '%${inv_code}%' or lower(mid.inv_name) like '%${inv_code}%' )
group by mad.group_id,mad.hos_id,mad.copy_code,mad.apply_id,mad.detail_id ) aa left join ( select group_id, hos_id, copy_code, rela_type, apply_id , app_detail_id, sum(nvl(rela_amount,0)) as rela_amount from mat_apply_out_rela
group_id = #{group_id,jdbcType=INTEGER} and hos_id = #{hos_id,jdbcType=INTEGER} and copy_code = #{copy_code,jdbcType=VARCHAR}
group by group_id, hos_id, copy_code, rela_type, apply_id, app_detail_id ) bb on aa.group_id=bb.group_id and aa.hos_id=bb.hos_id and aa.copy_code=bb.copy_code and aa.apply_id=bb.apply_id and aa.detail_id=bb.app_detail_id left join ( select group_id,hos_id,copy_code,rela_type,apply_id,app_detail_id,sum(nvl(rela_amount,0)) as rela_amount from mat_apply_pur_rela
group_id = #{group_id,jdbcType=INTEGER} and hos_id = #{hos_id,jdbcType=INTEGER} and copy_code = #{copy_code,jdbcType=VARCHAR}
group by group_id, hos_id, copy_code, rela_type, apply_id, app_detail_id ) cc on aa.group_id=cc.group_id and aa.hos_id=cc.hos_id and aa.copy_code=cc.copy_code and aa.apply_id=cc.apply_id and aa.detail_id=cc.app_detail_id group by aa.apply_id ) e on a.apply_id = e.apply_id WHERE a.group_id = #{group_id,jdbcType=INTEGER} and a.hos_id = #{hos_id,jdbcType=INTEGER} and a.copy_code = #{copy_code,jdbcType=VARCHAR}
and a.app_date >= #{begin_app_date,jdbcType=TIMESTAMP}
and a.app_date <= #{end_app_date,jdbcType=TIMESTAMP}
and a.dept_id = #{dept_id,jdbcType=INTEGER}
and a.dept_no = #{dept_no,jdbcType=INTEGER}
and a.brief like '%${brief}%'
and a.store_id = #{store_id,jdbcType=INTEGER}
and a.store_no = #{store_no,jdbcType=INTEGER}
and a.state = #{state,jdbcType=INTEGER}
AND a.apply_no like '%${apply_no}%'
and to_char(a.dept_id) in ( select perm_code from v_user_data_perm where group_id = #{group_id} and hos_id = #{hos_id} and user_id = #{user_id} and table_code = 'HOS_DEPT_DICT' and is_read = 1 and is_write = 1)
and ((to_char(a.store_id) in ( select perm_code from v_user_data_perm where group_id = #{group_id} and hos_id = #{hos_id} and user_id = #{user_id} and table_code = 'HOS_STORE_DICT' and is_read = 1 )) )
order by a.apply_id desc

mapper.update:
UPDATE mat_apply_main


		dept_id = #{dept_id,jdbcType=INTEGER},


		dept_no = #{dept_no,jdbcType=INTEGER},


		store_id = #{store_id,jdbcType=INTEGER},


		store_no = #{store_no,jdbcType=INTEGER},


		app_emp = #{app_emp,jdbcType=INTEGER},


		app_date = #{app_date,jdbcType=TIMESTAMP},


		brief = #{brief,jdbcType=VARCHAR},


		response_emp = #{response_emp,jdbcType=VARCHAR},


WHERE group_id =#{group_id,jdbcType=INTEGER}
and hos_id =#{hos_id,jdbcType=INTEGER}
and copy_code =#{copy_code,jdbcType=VARCHAR}
and apply_id =#{apply_id,jdbcType=INTEGER}

