and hos_id = #{item.hos_id,jdbcType=INTEGER}
and copy_code = #{item.copy_code,jdbcType=VARCHAR}
and apply_id = #{item.apply_id,jdbcType=INTEGER}
and detail_id = #{item.detail_id,jdbcType=INTEGER}

