mapper.select:
with out_temp as (select inv_id, batch_no, bar_code, price, location_id, sum(amount) as amount from (select b.inv_id, b.batch_no, b.bar_code, b.price, b.location_id, sum(nvl(b.amount, 0)) as amount from mat_out_main a left join mat_out_detail b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.copy_code = b.copy_code and a.out_id = b.out_id where a.group_id = #{group_id,jdbcType=INTEGER} and a.hos_id = #{hos_id,jdbcType=INTEGER} and a.copy_code =#{copy_code,jdbcType=VARCHAR} and a.store_id = #{store_id,jdbcType=INTEGER} AND a.state < 3 AND a.bus_type_code <> '21' AND b.amount_money > 0 group by b.inv_id, b.batch_no, b.bar_code, b.price, b.location_id union all select b.inv_id, b.batch_no, b.bar_code, b.price, b.location_id, -1 * sum(nvl(b.amount, 0)) as amount from mat_in_main a left join mat_in_detail b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.copy_code = b.copy_code and a.in_id = b.in_id and a.hos_id = #{hos_id,jdbcType=INTEGER} and a.copy_code = #{copy_code,jdbcType=VARCHAR} and a.store_id = #{store_id,jdbcType=INTEGER} AND a.state < 3 AND a.bus_type_code in (10,12,16,22) and b.amount_money < 0 group by b.inv_id, b.batch_no, b.bar_code, b.price, b.location_id) group by inv_id, batch_no, bar_code, price, location_id) select aa.mat_type_code,aa.mat_type_name,aa.inv_id, aa.inv_no, aa.inv_code, aa.inv_name, aa.inv_model, aa.unit_name, aa.price, aa.app_amount, aa.is_com, aa.bid_code, aa.fac_name, sum(aa.imme_amount) as imme_amount from (select distinct mtd.mat_type_code, mtd.mat_type_name, mid.inv_id, mid.inv_no, mid.inv_code, mid.inv_name, mid.inv_model, hu.unit_name, mid.plan_price as price, mdmd.amount as app_amount, mid.is_com, mid.bid_code, hfd.fac_name, nvl(mfb.left_amount, 0) - nvl(ot.amount, 0) as imme_amount from MAT_DEPT_MATCH mdm left join mat_dept_match_detail mdmd on mdm.group_id = mdmd.group_id and mdm.hos_id = mdmd.hos_id and mdm.copy_code = mdmd.copy_code and mdm.dept_match_id = mdmd.dept_match_id left join MAT_INV_DICT mid on mdmd.group_id = mid.group_id and mdmd.hos_id = mid.hos_id and mdmd.copy_code = mid.copy_code and mdmd.inv_id = mid.inv_id and mid.is_stop = 0 left join mat_type_dict mtd on mtd.group_id = mid.group_id and mtd.hos_id = mid.hos_id and mtd.copy_code = mid.copy_code and mtd.mat_type_id = mid.mat_type_id and mtd.is_stop = 0 left join mat_fifo_balance mfb on mid.group_id = mfb.group_id and mid.hos_id = mfb.hos_id and mid.copy_code = mfb.copy_code and mid.inv_id = mfb.inv_id
AND mfb.store_id = #{store_id}
left join out_temp ot on mfb.inv_id = ot.inv_id and mfb.batch_no = ot.batch_no and mfb.bar_code = ot.bar_code and mfb.price = ot.price and mfb.location_id = ot.location_id left join HOS_UNIT hu on mid.group_id = hu.group_id and mid.hos_id = hu.hos_id and mid.unit_code = hu.unit_code left join HOS_STORE_DICT hsd on mdm.group_id = hsd.group_id and mdm.hos_id = hsd.hos_id and mdm.store_id = hsd.store_id and hsd.is_stop = 0 left join HOS_FAC_DICT hfd on mid.group_id = hfd.group_id and mid.hos_id = hfd.hos_id and mid.fac_id = hfd.fac_id and mid.fac_no = hfd.fac_no
WHERE mdm.group_id = #{group_id} AND mdm.hos_id = #{hos_id} AND mdm.copy_code = #{copy_code}
AND mdm.dept_match_id = #{dept_match_id}
AND (mdm.store_id = #{store_id} or mdm.store_id is null)
AND mdm.dept_id = #{dept_id}
AND mid.use_state = 1 order by mid.inv_id) aa group by aa.mat_type_code,aa.mat_type_name,aa.inv_id, aa.inv_no, aa.inv_code, aa.inv_name, aa.inv_model, aa.unit_name, aa.price, aa.app_amount, aa.is_com, aa.bid_code, aa.fac_name

SELECT mad.inv_id, mid.inv_no, mid.inv_code, mid.inv_name, mid.bid_code, mid.inv_model, hfd.fac_name, hu.unit_name, mid.plan_price price, mid.is_com, sum(mad.app_amount) history_amount, 0 app_amount, decode(mid.is_com, 0, nvl(mih.cur_amount, 0), nvl(maih.cur_amount, 0)) cur_amount FROM mat_apply_main mam LEFT JOIN mat_apply_detail mad ON mam.group_id = mad.group_id AND mam.hos_id = mad.hos_id AND mam.copy_code = mad.copy_code AND mam.apply_id = mad.apply_id LEFT JOIN mat_inv_dict mid ON mad.group_id = mid.group_id AND mad.hos_id = mid.hos_id AND mad.copy_code = mid.copy_code AND mad.inv_id = mid.inv_id AND mid.is_stop = 0 and mid.use_state=1 LEFT JOIN hos_unit hu ON mid.group_id = hu.group_id AND mid.hos_id = hu.hos_id AND mid.unit_code = hu.unit_code LEFT JOIN hos_fac_dict hfd ON mid.group_id = hfd.group_id AND mid.hos_id = hfd.hos_id AND mid.fac_id = hfd.fac_id AND hfd.is_stop = 0 LEFT JOIN mat_inv_hold mih ON mam.group_id = mih.group_id AND mam.hos_id = mih.hos_id AND mam.copy_code = mih.copy_code AND mam.store_id = mih.store_id AND mad.inv_id = mih.inv_id LEFT JOIN mat_affi_inv_hold maih ON mam.group_id = maih.group_id AND mam.hos_id = maih.hos_id AND mam.copy_code = maih.copy_code AND mam.store_id = maih.store_id AND mad.inv_id = maih.inv_id WHERE mam.group_id = #{group_id,jdbcType=BIGINT} AND mam.hos_id = #{hos_id,jdbcType=BIGINT} AND mam.copy_code = #{copy_code,jdbcType=VARCHAR} AND mam.dept_id = #{dept_id,jdbcType=BIGINT}
AND mam.store_id = #{store_id,jdbcType=BIGINT}
AND mam.app_date >= #{begin_app_date,jdbcType=TIMESTAMP}
AND mam.app_date <= #{end_app_date,jdbcType=TIMESTAMP}
AND ( mid.inv_code like '${inv_code}%' OR mid.inv_name like '%${inv_code}%' OR mid.inv_model like '%${inv_code}%' OR mid.bid_code like '${inv_code}%' OR mid.alias like '${inv_code}%' OR mid.spell_code like '%${inv_code}%' OR mid.wbx_code like '%${inv_code}%' )
GROUP BY mad.inv_id, mid.inv_no, mid.inv_code, mid.inv_name, mid.bid_code, mid.inv_model, hfd.fac_name, hu.unit_name, mid.plan_price, mid.is_com, mih.cur_amount, maih.cur_amount ORDER BY mid.inv_name, mid.inv_code

