select null as group_id, null as hos_id, null as copy_code, null as apply_id, null as detail_id , null as inv_id, null as inv_no, null as inv_code, null as bid_code, null as inv_name , null as is_closed,null as inv_model,null as unit_name, null as fac_name, null as is_com , sum(a.app_amount) as app_amount,null as price, sum(a.app_amount * nvl(b.plan_price, 0)) as amount_money , null as note, null as is_sec_whg , '合计' as apply_no, null as store_name , null as app_date, null as brief, null as dept_name, null as maker , null as emp_name, null as state, null as back_reason, null as response_emp,null as sender from mat_apply_detail a left join mat_apply_main mam on a.group_id=mam.group_id and a.hos_id=mam.hos_id and a.copy_code=mam.copy_code and a.apply_id=mam.apply_id left join mat_inv_dict b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.copy_code = b.copy_code and a.inv_id = b.inv_id and a.inv_no = b.inv_no
a.group_id = #{group_id} and a.hos_id = #{hos_id} and a.copy_code = #{copy_code}
and mam.app_date >= #{begin_app_date,jdbcType=TIMESTAMP}
and mam.app_date <= #{end_app_date,jdbcType=TIMESTAMP}
and mam.dept_id = #{dept_id,jdbcType=INTEGER}
and mam.dept_no = #{dept_no,jdbcType=INTEGER}
and mam.brief like '%${brief}%'
and mam.store_id = #{store_id,jdbcType=INTEGER}
and mam.store_no = #{store_no,jdbcType=INTEGER}
and mam.state = #{state,jdbcType=INTEGER}
AND mam.apply_no like '%${apply_no}%'
AND ( b.inv_code like '%${inv_code}%' or upper(b.inv_name) like '%${inv_code}%' or upper(b.inv_model) like '%${inv_code}%' or upper(b.spell_code) like '%${inv_code}%' or upper(b.wbx_code) like '%${inv_code}%' or upper(b.bid_code) like '%${inv_code}%' or lower(b.spell_code) like '%${inv_code}%' or lower(b.bid_code) like '%${inv_code}%' or lower(b.wbx_code) like '%${inv_code}%' or lower(b.inv_name) like '%${inv_code}%'
)
UNION ALL SELECT temp.* FROM ( select a.group_id, a.hos_id, a.copy_code, a.apply_id, a.detail_id , a.inv_id, a.inv_no, b.inv_code, b.bid_code, b.inv_name , nvl(a.is_closed, 0) as is_closed, b.inv_model, c.unit_name, d.fac_name, b.is_com , a.app_amount,nvl(b.plan_price,0) as price, a.app_amount * nvl(b.plan_price, 0) as amount_money , a.note, b.is_sec_whg , to_char(mam.apply_no) as apply_no, hsd.store_name , to_char(mam.app_date, 'yyyy-MM-dd hh24:mi:ss') as app_date, mam.brief, bb.dept_name, su.user_name as maker , cc.emp_name, mam.state, mam.back_reason, res.emp_name as response_emp,sender.user_name as sender
from mat_apply_detail a left join mat_apply_main mam on a.group_id=mam.group_id and a.hos_id=mam.hos_id and a.copy_code=mam.copy_code and a.apply_id=mam.apply_id left join mat_inv_dict b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.copy_code = b.copy_code and a.inv_id = b.inv_id and a.inv_no=b.inv_no left join hos_unit c on b.group_id = c.group_id and b.hos_id = c.hos_id and b.unit_code = c.unit_code left join hos_fac_dict d on b.group_id = d.group_id and b.hos_id = d.hos_id and b.fac_id = d.fac_id and b.fac_no = d.fac_no left join hos_store_dict hsd on mam.group_id = hsd.group_id and mam.hos_id = hsd.hos_id and mam.store_id = hsd.store_id and hsd.is_stop = 0 left join hos_dept_dict bb on mam.group_id = bb.group_id and mam.hos_id = bb.hos_id and mam.dept_id = bb.dept_id and bb.is_stop = 0 left join hos_emp_dict cc on mam.group_id = cc.group_id and mam.hos_id = cc.hos_id and mam.app_emp = cc.emp_id and cc.is_stop = 0 left join sys_user su on mam.group_id = su.group_id and mam.hos_id = su.hos_id and mam.maker = su.user_id and su.is_stop = 0 left join sys_user sender on mam.group_id = sender.group_id and mam.hos_id = sender.hos_id and mam.sender = sender.user_id and su.is_stop = 0
left join hos_emp_dict res on mam.group_id = res.group_id and mam.hos_id = res.hos_id and mam.response_emp = res.emp_id and res.is_stop = 0
a.group_id = #{group_id} and a.hos_id = #{hos_id} and a.copy_code = #{copy_code}
and mam.app_date >= #{begin_app_date,jdbcType=TIMESTAMP}
and mam.app_date <= #{end_app_date,jdbcType=TIMESTAMP}
and mam.dept_id in ( select perm_code from v_user_data_perm where group_id = ${group_id} and hos_id = ${hos_id} and user_id = ${user_id} and table_code = 'HOS_DEPT_DICT' and is_read = 1 and is_write = 1)
and ((mam.store_id in ( select perm_code from v_user_data_perm where group_id = ${group_id} and hos_id = ${hos_id} and user_id = ${user_id} and table_code = 'HOS_STORE_DICT' and is_read = 1 )) )
and mam.dept_id = #{dept_id,jdbcType=INTEGER}
and mam.dept_no = #{dept_no,jdbcType=INTEGER}
and mam.brief like '%${brief}%'
and mam.store_id = #{store_id,jdbcType=INTEGER}
and mam.store_no = #{store_no,jdbcType=INTEGER}
and mam.state = #{state,jdbcType=INTEGER}
AND mam.apply_no like '%${apply_no}%'
AND ( b.inv_code like '%${inv_code}%' or upper(b.inv_name) like '%${inv_code}%' or upper(b.inv_model) like '%${inv_code}%' or upper(b.spell_code) like '%${inv_code}%' or upper(b.wbx_code) like '%${inv_code}%' or upper(b.bid_code) like '%${inv_code}%' or lower(b.spell_code) like '%${inv_code}%' or lower(b.bid_code) like '%${inv_code}%' or lower(b.wbx_code) like '%${inv_code}%' or lower(b.inv_name) like '%${inv_code}%'
)
)temp
