SELECT 
	group_id,
	hos_id,
	copy_code,
	store_id,
	inv_id,
	cur_amount,
	cur_money,
	location_id
FROM MED_INV_HOLD 


	AND group_id = #{group_id,jdbcType=INTEGER}


	AND hos_id = #{hos_id,jdbcType=INTEGER}


	AND copy_code = #{copy_code,jdbcType=VARCHAR}


	AND store_id = #{store_id,jdbcType=INTEGER}


	AND inv_id = #{inv_id,jdbcType=INTEGER}


	AND cur_amount = #{cur_amount,jdbcType=INTEGER}


	AND cur_money = #{cur_money,jdbcType=INTEGER}


	AND location_id = #{location_id,jdbcType=INTEGER}


order by group_id asc

with list_temp as (
select #{item.group_id,jdbcType=INTEGER} as group_id, #{item.hos_id,jdbcType=INTEGER} as hos_id, #{item.copy_code,jdbcType=VARCHAR} as copy_code, #{item.store_id,jdbcType=INTEGER} as store_id, #{item.inv_id,jdbcType=INTEGER} as inv_id, #{item.location_id,jdbcType=INTEGER} as location_id from dual
) select a.group_id, a.hos_id, a.copy_code, a.store_id, a.inv_id, nvl(b.cur_amount, 0) as cur_amount, nvl(b.cur_money, 0) as cur_money, nvl(b.location_id, 0) as location_id from list_temp a inner join med_inv_hold b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.copy_code = b.copy_code and a.store_id = b.store_id and a.inv_id = b.inv_id and nvl(a.location_id, 0) = nvl(b.location_id, 0) order by a.store_id, a.inv_id
