	and hos_id = #{hos_id}   
	and copy_code = #{copy_code}   
	and cert_id = #{cert_id}

DELETE FROM med_inv_cert WHERE

		group_id = #{item.group_id}
		 and 
		hos_id = #{item.hos_id}
		 and 
		copy_code = #{item.copy_code}
		 and 
		cert_id = #{item.cert_id}

mapper.select:

SELECT 
	group_id,
	hos_id,
	copy_code,
	cert_id,
	cert_code,
	cert_inv_name,
	type_id,
	fac_id,
	cert_date,
	issuing_authority,
	start_date,
	end_date,
	link_person,
	link_tel,
	link_mobile,
	cert_memo,
	file_path,
	file_address,
	cert_state
FROM MED_INV_CERT


AND group_id = #{group_id}


AND hos_id = #{hos_id}


AND copy_code = #{copy_code}


AND cert_id = #{cert_id}


AND cert_code like '%${cert_code}%'


AND cert_inv_name like '%${cert_inv_name}%'


AND type_id = #{type_id}


AND fac_id = #{fac_id}


AND cert_date = #{cert_date}


AND issuing_authority = #{issuing_authority}


AND start_date = #{start_date}


AND end_date = #{end_date}


AND link_person = #{link_person}


AND link_tel = #{link_tel}


AND link_mobile = #{link_mobile}


AND cert_memo = #{cert_memo}


AND file_path = #{file_path}


AND file_address = #{file_address}


AND cert_state = #{cert_state}


order by group_id asc

SELECT group_id, hos_id, copy_code, cert_id, cert_code, cert_inv_name, type_id, fac_id, cert_date, issuing_authority, start_date, end_date, link_person, link_tel, link_mobile, cert_memo, file_path, file_address, cert_state FROM med_inv_cert
AND group_id = #{group_id}
AND hos_id = #{hos_id}
AND copy_code = #{copy_code}
AND cert_id = #{cert_id}
AND cert_code = #{cert_code}


SELECT 
group_id,
hos_id,
copy_code,
cert_id,
cert_code,
cert_inv_name,
type_id,
fac_id,
cert_date,
issuing_authority,
start_date,
end_date,
link_person,
link_tel,
link_mobile,
cert_memo,
file_path,
file_address,
cert_state
FROM MED_INV_CERT 


AND group_id = #{group_id}


AND hos_id = #{hos_id}


AND copy_code = #{copy_code}


AND cert_id = #{cert_id}


AND cert_code = #{cert_code}


AND cert_inv_name = #{cert_inv_name}


AND type_id = #{type_id}


AND fac_id = #{fac_id}


AND cert_date = #{cert_date}


AND issuing_authority = #{issuing_authority}


AND start_date = #{start_date}


AND end_date = #{end_date}


AND link_person = #{link_person}


AND link_tel = #{link_tel}


AND link_mobile = #{link_mobile}


AND cert_memo = #{cert_memo}


AND file_path = #{file_path}


AND file_address = #{file_address}


AND cert_state = #{cert_state}


order by group_id asc

SELECT * FROM (
	SELECT 
		mic.group_id,
		mic.hos_id,
		mic.copy_code,
		mic.cert_id,
		mic.cert_code,
		mic.cert_inv_name,
		mic.type_id,
		mict.type_name,
		mic.fac_id,
		hfd.fac_name,
		mic.cert_date,
		mic.issuing_authority,
		mic.start_date,
		mic.end_date,
		mic.link_person,
		mic.link_tel,
		mic.link_mobile,
		mic.cert_memo,
		mic.file_path,
		mic.file_address,
		mic.cert_state, 
		hsd.sup_name,
		mic.state
	FROM MED_INV_CERT mic  
	left join MED_INV_CERT_TYPE mict
		ON 
			mic.group_id = mict.group_id  and 
			mic.hos_id = mict.hos_id  and
			mic.copy_code = mict.copy_code  and
			mic.type_id = mict.type_id  
	left join HOS_FAC_DICT hfd 
		ON 
			mic.group_id = hfd.group_id  and 
			mic.hos_id = hfd.hos_id  and
			mic.fac_id = hfd.fac_id  and
			hfd.is_stop = 0

	LEFT JOIN  hos_sup_dict hsd 
		ON mic.group_id = hsd.group_id 
		AND mic.hos_id = hsd.hos_id 
		AND mic.sup_id = hsd.sup_id 
		AND hsd.is_stop = 0


	AND mic.group_id = #{group_id}


	AND mic.hos_id = #{hos_id}


	AND mic.copy_code = #{copy_code}


	AND mic.cert_id = #{cert_id}


	AND mic.cert_code like '%${cert_code}%'


	AND mic.cert_inv_name like '%${cert_inv_name}%'


	AND mic.type_id = #{type_id}


	AND mic.fac_id = #{fac_id}


	AND mic.cert_date = #{cert_date}


	AND mic.issuing_authority = #{issuing_authority}


	AND mic.start_date = #{start_date}


	AND mic.end_date = #{end_date}


	AND mic.link_person = #{link_person}


	AND mic.link_tel = #{link_tel}


	AND mic.link_mobile = #{link_mobile}


	AND mic.cert_memo = #{cert_memo}


	AND mic.file_path = #{file_path}


	AND mic.file_address = #{file_address}


	AND mic.cert_state = #{cert_state}


	AND mic.state = #{state}


		AND hsd.sup_id = #{sup_id}


)

	order by ${sortname} ${sortorder}


	order by cert_id asc

SELECT mic.group_id, mic.hos_id, mic.copy_code, mic.cert_id, mic.cert_code, mic.cert_inv_name, mic.type_id, mict.type_code, mict.type_name, mic.fac_id, hfd.fac_code, hfd.fac_name, mic.cert_date, mic.issuing_authority, mic.start_date, mic.end_date, mic.link_person, mic.link_tel, mic.link_mobile, mic.cert_memo, mic.file_path, mic.file_address, mic.cert_state, mic.sup_id, hsd.sup_code, hsd.sup_name FROM MED_INV_CERT mic left join MED_INV_CERT_TYPE mict ON mic.group_id = mict.group_id and mic.hos_id = mict.hos_id and mic.copy_code = mict.copy_code and mic.type_id = mict.type_id left join HOS_FAC_DICT hfd ON mic.group_id = hfd.group_id and mic.hos_id = hfd.hos_id and mic.fac_id = hfd.fac_id and hfd.is_stop = 0 left join hos_sup_dict hsd on mic.group_id = hsd.group_id and mic.hos_id = hsd.hos_id and mic.sup_id = hsd.sup_id and hsd.is_stop = 0
AND mic.group_id = #{group_id}
AND mic.hos_id = #{hos_id}
AND mic.copy_code = #{copy_code}
AND mic.cert_id = #{cert_id}
order by mic.group_id asc


SELECT 
cert_code
FROM med_inv_cert 


	AND group_id = #{group_id}


	AND hos_id = #{hos_id}


	AND copy_code = #{copy_code}


	AND cert_id != #{cert_id}


	AND cert_code = #{cert_code}


SELECT count(cert_id)
FROM MED_INV_CERT_RELA 


AND group_id = #{group_id}


AND hos_id = #{hos_id}


AND copy_code = #{copy_code}


AND cert_id = #{cert_id}

select mid.group_id , mid.hos_id , mid.copy_code, mid.inv_id, mid.inv_no, mid.inv_code, mid.inv_name, mid.unit_code, hu.unit_name, mid.inv_model, hfd.fac_name, hspd.sup_name, mid.med_type_id, mtd.med_type_code, mid.bid_code, mr.cert_code, mid.PLAN_PRICE
,hsd.store_id, hsd.store_code
from med_inv_dict mid left join ( select inv_id,group_id,hos_id,copy_code,min(cert_id) cert_id,cert_code from med_inv_cert_rela group by inv_id,group_id,hos_id,copy_code, cert_id,cert_code ) mr on mr.inv_id=mid.inv_id left join hos_unit hu on mid.group_id = hu.group_id and mid.hos_id = hu.hos_id and mid.unit_code = hu.unit_code and hu.is_stop = 0 left join hos_fac_dict hfd on hfd.group_id = mid.group_id and hfd.hos_id = mid.hos_id and hfd.fac_id = mid.fac_id and hfd.fac_no = mid.fac_no left join med_inv_sup mis on mis.group_id = mid.group_id and mis.hos_id = mid.hos_id and mis.copy_code = mid.copy_code and mis.inv_id = mid.inv_id and mis.is_default = 1 left join hos_sup_dict hspd on hspd.group_id = mid.group_id and hspd.hos_id = mid.hos_id and hspd.sup_id = mis.sup_id and hspd.is_stop = 0 left join med_type_dict mtd on mid.group_id=mtd.group_id and mid.hos_id=mtd.hos_id and mid.copy_code=mtd.copy_code and mid.med_type_id=mtd.med_type_id and mtd.is_stop=0
left join MED_STORE_INV msi on mid.group_id=msi.group_id and mid.hos_id=msi.hos_id and mid.copy_code=msi.copy_code and mid.inv_id=msi.inv_id left join HOS_STORE_DICT hsd on mid.group_id=hsd.group_id and mid.hos_id=hsd.hos_id and msi.store_id=hsd.store_id
and mid.group_id = #{group_id,jdbcType=INTEGER}
and mid.hos_id = #{hos_id,jdbcType=INTEGER}
and mid.copy_code = #{copy_code,jdbcType=VARCHAR}
and mid.inv_model like '%${inv_model}%'
and mid.bid_code like '%${bid_code}%'
and mid.fac_id = #{fac_id,jdbcType=INTEGER}
and ( mid.inv_code like '${key}%' or mid.inv_name like '%${key}%' or mid.spell_code like upper('%${key}%') or
mid.alias like '%${key}%' or mid.bid_code like '${key}%' )
and mid.is_stop = 0
and ( mid.inv_code like '${inv_msg}%' or mid.inv_name like '%${inv_msg}%' or mid.spell_code like upper('%${inv_msg}%') or
mid.alias like '%${inv_msg}%' or mid.bid_code like '${inv_msg}%' )
and mtd.med_type_code=#{med_type_code,jdbcType=VARCHAR}
and hsd.store_id=#{store_id,jdbcType=VARCHAR}
and hspd.sup_id=#{sup_id,jdbcType=INTEGER}
and mid.inv_id not in ( select distinct inv_id from med_inv_cert_rela where group_id = #{group_id,jdbcType=INTEGER} and hos_id = #{hos_id,jdbcType=INTEGER} and copy_code = #{copy_code,jdbcType=VARCHAR} and cert_id = #{cert_id,jdbcType=INTEGER} )

update.foreach:
update med_inv_cert set 
	state = #{item.state,jdbcType=INTEGER}

WHERE
	group_id = #{item.group_id,jdbcType=INTEGER}
	and hos_id = #{item.hos_id,jdbcType=INTEGER}
	and copy_code = #{item.copy_code,jdbcType=VARCHAR}
	and cert_id = #{item.cert_id,jdbcType=INTEGER}

mapper.select:
SELECT cert_id, cert_code 
FROM med_inv_cert 
WHERE group_id = #{group_id,jdbcType=INTEGER}
	AND hos_id = #{hos_id,jdbcType=INTEGER}
	AND copy_code = #{copy_code,jdbcType=VARCHAR}

SELECT type_id, type_code, type_name 
FROM med_inv_cert_type 
WHERE group_id = #{group_id,jdbcType=INTEGER}
	AND hos_id = #{hos_id,jdbcType=INTEGER}
	AND copy_code = #{copy_code,jdbcType=VARCHAR}

SELECT fac_id, fac_code, fac_name 
FROM hos_fac 
WHERE group_id = #{group_id,jdbcType=INTEGER}
	AND hos_id = #{hos_id,jdbcType=INTEGER}
	AND is_stop = 0

SELECT inv_id, inv_code, inv_name 
FROM med_inv 
WHERE group_id = #{group_id,jdbcType=INTEGER}
	AND hos_id = #{hos_id,jdbcType=INTEGER}
	AND copy_code = #{copy_code,jdbcType=VARCHAR}
	AND use_state = 1

SELECT sup_id, sup_code, sup_name 
FROM hos_sup 
WHERE group_id = #{group_id,jdbcType=INTEGER}
	AND hos_id = #{hos_id,jdbcType=INTEGER}
	AND is_stop = 0
