mapper.insert:
INSERT INTO MED_STOCK_TYPE ( group_id , hos_id , copy_code , stock_type_code , stock_type_name , wbx_code , spell_code , is_stop
)
select #{item.group_id} , #{item.hos_id} , #{item.copy_code} , #{item.stock_type_code} , #{item.stock_type_name} , #{item.wbx_code} , #{item.spell_code} , #{item.is_stop} from dual

mapper.update:
UPDATE med_stock_type SET
	stock_type_name = #{stock_type_name},
	wbx_code = #{wbx_code} ,
	spell_code = #{spell_code} ,
	is_stop = #{is_stop} 

WHERE group_id = #{group_id}
	and hos_id = #{hos_id}
	and copy_code = #{copy_code}
	and stock_type_code = #{stock_type_code}

update.foreach:
UPDATE med_stock_type SET
	stock_type_name = #{item.stock_type_name},
	wbx_code = #{item.wbx_code},
	spell_code = #{item.spell_code},
	is_stop = #{item.is_stop}

WHERE  group_id = #{item.group_id}
and  hos_id = #{item.hos_id}
and copy_code = #{item.copy_code}
and stock_type_code = #{item.stock_type_code}

mapper.delete:

DELETE FROM med_stock_type 
WHERE group_id = #{group_id}   
	and  hos_id = #{hos_id}   
	and  copy_code = #{copy_code}   
	and  stock_type_code = #{stock_type_code}

DELETE FROM med_stock_type WHERE

		group_id = #{item.group_id}
		and hos_id = #{item.hos_id}
		and copy_code = #{item.copy_code}
		and stock_type_code = #{item.stock_type_code}

mapper.select:
SELECT
stock_type_code, stock_type_name,
is_stop FROM MED_STOCK_TYPE
AND group_id = #{group_id}
AND hos_id = #{hos_id}
AND copy_code = #{copy_code}
AND stock_type_code like #{stock_type_code}||'%'
and (stock_type_name like '%'||#{stock_type_name}||'%' or upper(spell_code) like '%'||#{stock_type_name}||'%' or upper(wbx_code) like '%'||#{stock_type_name}||'%' or lower(spell_code) like '%'||#{stock_type_name}||'%' or lower(wbx_code) like '%'||#{stock_type_name}||'%')
AND wbx_code = #{wbx_code}
AND spell_code = #{spell_code}
order by group_id asc


SELECT 
	group_id,
	hos_id,
	copy_code,
	stock_type_code,
	stock_type_name,
	wbx_code,
	spell_code,
	is_stop,
	note
FROM MED_STOCK_TYPE 


AND group_id = #{group_id}


AND hos_id = #{hos_id}


AND copy_code = #{copy_code}


AND stock_type_code = #{stock_type_code}


AND stock_type_name = #{stock_type_name}


AND wbx_code = #{wbx_code}


AND spell_code = #{spell_code}


AND is_stop = #{is_stop}


AND note = #{note}


order by group_id asc
