update.foreach:
UPDATE med_require_main 
SET state = 3
WHERE	group_id =	#{item.group_id,jdbcType=INTEGER}
	and		hos_id = #{item.hos_id,jdbcType=INTEGER}
	and		copy_code = #{item.copy_code,jdbcType=VARCHAR}
	and		req_id =#{item.req_id,jdbcType=INTEGER}

mapper.select:
select mrm.group_id, mrm.copy_code, mrm.hos_id, mrd.inv_id, mrd.inv_no, mrd.sup_no, mrd.sup_id, mrd.price, mrd.pack_code, mrd.num_exchange, sum(nvl(mrd.amount,0)) amount from med_require_main mrm left join med_require_detail mrd on mrd.req_id = mrm.req_id and mrd.req_code = mrm.req_code and mrd.hos_id = mrm.hos_id and mrd.group_id = mrm.group_id and mrd.copy_code = mrm.copy_code
mrd.inv_id is not null and mrm.state = 2 and mrm.make_date between to_date(#{begin_date},'yyyy/MM/dd') and to_date(#{end_date},'yyyy/MM/dd')
and mrm.group_id = #{group_id,jdbcType=INTEGER}
and mrm.hos_id = #{hos_id,jdbcType=INTEGER}
and mrm.copy_code = #{copy_code,jdbcType=VARCHAR}
and mrm.stock_id = #{store_id,jdbcType=INTEGER}
and mrm.stock_no = #{store_no,jdbcType=INTEGER}
and mrm.dept_id = #{dept_id,jdbcType=INTEGER}
and mrm.dept_no = #{dept_no,jdbcType=INTEGER}
group by mrm.group_id,mrm.copy_code,mrm.hos_id,mrd.inv_id,mrd.inv_no, mrd.sup_no,mrd.sup_id,mrd.price,mrd.pack_code,mrd.num_exchange order by mrd.inv_id

SELECT mrm.group_id, mrm.hos_id, mrm.copy_code, mrm.req_id, mrm.req_code, mrm.dept_id, mrm.dept_no, hdd.dept_code, hdd.dept_name, mrd.inv_id, mrd.inv_no, mid.inv_code, mid.inv_name, mid.inv_model, mid.unit_code, hu.unit_name, hsd.sup_name, nvl(mih.cur_amount,0) cur_amount,--当前库存 nvl(mrd.amount,0) req_amount,--需求数量 case when mrm.state=2 then nvl(mrd.amount, 0) else nvl(mrr.exec_amount, 0) end as exec_amount,--执行数量 mrd.price price,--价格 mrd.price * nvl(mrd.amount,0) req_money,--需求金额 mrm.state --状态 FROM med_require_main mrm LEFT JOIN med_require_detail mrd ON mrm.group_id = mrd.group_id AND mrm.hos_id = mrd.hos_id AND mrm.copy_code = mrd.copy_code AND mrm.req_id = mrd.req_id AND mrm.req_code = mrd.req_code LEFT JOIN (select req_id,inv_id,sum(exec_amount) as exec_amount from med_require_rela
AND group_id = #{group_id,jdbcType=INTEGER}
AND hos_id = #{hos_id,jdbcType=INTEGER}
AND copy_code = #{copy_code,jdbcType=VARCHAR}
group by req_id,inv_id ) mrr ON mrm.req_id = mrr.req_id AND mrd.inv_id = mrr.inv_id LEFT JOIN hos_dept_dict hdd ON hdd.dept_id = mrm.dept_id AND hdd.dept_no = mrm.dept_no AND hdd.group_id = mrm.group_id AND hdd.hos_id = mrm.hos_id AND hdd.is_stop = 0 LEFT JOIN med_inv_dict mid ON mid.group_id = mrd.group_id AND mid.copy_code = mrd.copy_code AND mid.hos_id = mrd.hos_id AND mid.inv_id = mrd.inv_id AND mid.inv_no = mrd.inv_no AND mid.is_stop = 0 LEFT JOIN ( SELECT sup_id, inv_id, group_id, hos_id, copy_code,is_default FROM med_inv_sup WHERE is_default = 1 ) mis on mid.group_id = mis.group_id and mid.hos_id = mis.hos_id and mid.copy_code = mis.copy_code and mid.inv_id = mis.inv_id LEFT JOIN hos_sup_dict hsd ON mis.group_id = hsd.group_id AND mis.hos_id = hsd.hos_id AND mis.sup_id = hsd.sup_id AND hsd.is_stop = 0 LEFT JOIN hos_unit hu ON hu.group_id = mid.group_id AND hu.hos_id = mid.hos_id AND hu.unit_code = mid.unit_code AND hu.is_stop = 0 LEFT JOIN med_inv_hold mih ON mih.group_id = mrm.group_id AND mih.hos_id = mrm.hos_id AND mih.copy_code = mrm.copy_code AND mih.inv_id = mrd.inv_id AND mih.store_id = mrm.stock_id
and mrm.group_id = #{group_id,jdbcType=INTEGER}
AND mrm.hos_id = #{hos_id,jdbcType=INTEGER}
AND mrm.copy_code = #{copy_code,jdbcType=VARCHAR}
AND mrm.stock_id = #{store_id,jdbcType=INTEGER}
AND mrm.stock_no = #{store_no,jdbcType=INTEGER}
AND mrm.dept_id = #{dept_id,jdbcType=INTEGER}
AND mrm.dept_no = #{dept_no,jdbcType=INTEGER}
AND mrm.req_code like #{req_code,jdbcType=VARCHAR}||'%'
and( mid.inv_code like '%'||#{inv_code}||'%' or mid.inv_name like '%'||#{inv_code}||'%' or mid.spell_code like '%'||#{inv_code}||'%' or mid.wbx_code like '%'||#{inv_code}||'%' )
AND mrm.make_date BETWEEN to_date(#{begin_date}, 'yyyy/MM/dd') AND to_date(#{end_date}, 'yyyy/MM/dd') AND mrm.state = 2
order by mrm.req_code desc

with med_req_table as( SELECT mrm.group_id, mrm.hos_id, mrm.copy_code,mrm.req_id, mrm.state, mrm.dept_id, mrm.dept_no, mrm.stock_id, mrm.stock_no, mrd.inv_id, mrd.inv_no, mrd.price, nvl(mrd.amount, 0) AS amount, case when mrm.state=2 then nvl(mrd.amount, 0) else nvl(mrr.exec_amount, 0) end as exec_amount FROM med_require_main mrm LEFT JOIN med_require_detail mrd ON mrm.group_id = mrd.group_id AND mrm.hos_id = mrd.hos_id AND mrm.copy_code = mrd.copy_code AND mrm.req_id = mrd.req_id AND mrm.req_code = mrd.req_code LEFT JOIN (select req_id,inv_id,sum(exec_amount) as exec_amount from med_require_rela
and group_id = #{group_id,jdbcType=INTEGER}
AND hos_id = #{hos_id,jdbcType=INTEGER}
AND copy_code = #{copy_code,jdbcType=VARCHAR}
group by req_id,inv_id ) mrr ON mrm.req_id = mrr.req_id AND mrd.inv_id = mrr.inv_id
and mrm.group_id = #{group_id,jdbcType=INTEGER}
AND mrm.hos_id = #{hos_id,jdbcType=INTEGER}
AND mrm.copy_code = #{copy_code,jdbcType=VARCHAR}
AND mrm.stock_id = #{store_id,jdbcType=INTEGER}
AND mrm.make_date BETWEEN to_date(#{begin_date}, 'yyyy/MM/dd') AND to_date(#{end_date}, 'yyyy/MM/dd')
AND mrm.state > 1
AND mrm.state = 3
AND mrm.state = 2
)
SELECT mrt.group_id , mrt.hos_id , mrt.copy_code , mrt.stock_id , mrt.stock_no , mrt.inv_id , mrt.inv_no , mid.inv_code , mid.inv_name , mid.inv_model , mid.unit_code , hu.unit_name , hsd.sup_name , nvl(mih.cur_amount, 0) AS cur_amount , nvl(mrt.amount, 0) AS req_amount , nvl(mrt.exec_amount, 0) AS exec_amount , mrt.price AS price , mrt.price * nvl(mrt.amount, 0) AS req_money , mrt.state AS state
, detail.detail_data AS detail_data
FROM (SELECT a.group_id , a.hos_id , a.copy_code , a.stock_id , a.stock_no , a.inv_id , a.inv_no , a.price , sum(nvl(a.amount, 0)) AS amount , sum(nvl(exec_amount, 0)) AS exec_amount , a.state AS state FROM med_req_table a group by a.group_id, a.hos_id, a.copy_code, a.stock_id, a.stock_no, a.inv_id, a.inv_no, a.price ,a.state ) mrt LEFT JOIN med_inv_dict mid ON mid.group_id = mrt.group_id AND mid.copy_code = mrt.copy_code AND mid.hos_id = mrt.hos_id AND mid.inv_id = mrt.inv_id AND mid.inv_no = mrt.inv_no AND mid.is_stop = 0 LEFT JOIN ( SELECT sup_id, inv_id, group_id, hos_id, copy_code,is_default FROM med_inv_sup WHERE is_default = 1 ) mis on mid.group_id = mis.group_id and mid.hos_id = mis.hos_id and mid.copy_code = mis.copy_code and mid.inv_id = mis.inv_id LEFT JOIN hos_sup_dict hsd ON mis.group_id = hsd.group_id AND mis.hos_id = hsd.hos_id AND mis.sup_id = hsd.sup_id AND hsd.is_stop = 0 LEFT JOIN hos_unit hu ON hu.group_id = mid.group_id AND hu.hos_id = mid.hos_id AND hu.unit_code = mid.unit_code AND hu.is_stop = 0 LEFT JOIN med_inv_hold mih ON mih.group_id = mrt.group_id AND mih.hos_id = mrt.hos_id AND mih.copy_code = mrt.copy_code AND mih.inv_id = mrt.inv_id AND mih.store_id = mrt.stock_id
LEFT JOIN ( SELECT a.inv_id, a.inv_no, SUM(nvl(a.amount, 0)) AS amount, '[' || to_char(WM_CONCAT('{"req_id":' || a.req_id || ',"inv_id":' || a.inv_id || ',"inv_no":' || a.inv_no || ',"exec_amount":' || a.amount || ',"req_amount":' || a.amount || '}')) || ']' AS detail_data FROM med_req_table a GROUP BY a.inv_id, a.inv_no ) detail ON mid.inv_id = detail.inv_id AND mid.inv_no = detail.inv_no
ORDER BY mid.inv_code ASC

SELECT mrm.group_id, mrm.hos_id, mrm.copy_code, mrm.req_id req_id, mrm.req_code req_code, mrm.dept_no, mrm.dept_id, hd.dept_name dept_name, nvl(mrd.price, 0) price, nvl(mrd.amount, 0) req_amount, case when mrm.state=2 then nvl(mrd.amount, 0) else nvl(mrr.exec_amount, 0) end as exec_amount, nvl(mrd.price, 0) * nvl(mrd.amount, 0) req_money, mrd.inv_id, mrd.inv_no FROM med_require_main mrm LEFT JOIN med_require_detail mrd ON mrd.group_id = mrm.group_id AND mrd.hos_id = mrm.hos_id AND mrd.copy_code = mrm.copy_code AND mrd.req_id = mrm.req_id LEFT JOIN hos_dept_dict hd ON hd.dept_no = mrm.dept_no AND hd.dept_id = mrm.dept_id AND hd.group_id = mrm.group_id AND hd.hos_id = mrm.hos_id AND hd.is_stop = 0 LEFT JOIN hos_store_dict hs ON hs.store_no = mrm.stock_no AND hs.store_id = mrm.stock_id AND hs.group_id = mrm.group_id AND hs.hos_id = mrm.hos_id AND hs.is_stop = 0 LEFT JOIN med_inv_dict mid ON mid.inv_no = mrd.inv_no AND mid.inv_id = mrd.inv_id AND mid.group_id = mrd.group_id AND mid.copy_code = mrd.copy_code AND mid.hos_id = mrd.hos_id AND mid.is_stop = 0 LEFT JOIN (select req_id,inv_id,sum(exec_amount) as exec_amount from med_require_rela
and group_id = #{group_id,jdbcType=INTEGER}
AND hos_id = #{hos_id,jdbcType=INTEGER}
AND copy_code = #{copy_code,jdbcType=VARCHAR}
group by req_id,inv_id ) mrr ON mrm.req_id = mrr.req_id AND mrd.inv_id = mrr.inv_id
mrm.group_id = #{group_id,jdbcType=INTEGER}
AND mrm.hos_id = #{hos_id,jdbcType=INTEGER}
AND mrm.copy_code = #{copy_code,jdbcType=VARCHAR}
AND mid.inv_id = #{inv_id,jdbcType=INTEGER}
AND mid.inv_no = #{inv_no,jdbcType=INTEGER}
AND mrm.stock_id = #{store_id,jdbcType=INTEGER}
AND mrm.stock_no = #{store_no,jdbcType=INTEGER}
AND mrm.make_date >= to_date(#{begin_date},'yyyy/MM/dd')
AND mrm.make_date <= to_date(#{end_date},'yyyy/MM/dd')
AND mrm.state = #{state,jdbcType=VARCHAR}
order by mrm.req_id

select mrm.req_id, pum.pur_id, mrm.req_code, pum.pur_code, hsd.store_name, to_char(mrm.make_date,'yyyy-MM-dd') make_date, su.user_name maker, mrm.state state, '查看明细' other_inv from med_require_main mrm left join hos_store_dict hsd on mrm.hos_id = hsd.hos_id and mrm.group_id = hsd.group_id and mrm.stock_no = hsd.store_no and hsd.store_id = mrm.stock_id and hsd.is_stop = 0 left join sys_user su on su.user_id = mrm.maker and su.copy_code = mrm.copy_code and su.group_id = mrm.group_id and su.hos_id = mrm.hos_id left join med_req_pur_rela mrpr on mrpr.req_id = mrm.req_id and mrpr.hos_id = mrm.hos_id and mrpr.group_id = mrm.hos_id and mrpr.copy_code = mrm.copy_code left join med_pur_main pum on pum.pur_id = mrpr.pur_id and pum.copy_code = mrpr.copy_code and pum.hos_id = mrpr.hos_id and pum.group_id = mrpr.group_id
mrm.hos_id = #{hos_id}
and mrm.group_id = #{group_id}
and mrm.copy_code = #{copy_code}
and mrm.stock_id = #{store_id}
and mrm.stock_no = #{store_no}
and mrm.req_code like #{req_code}||'%'
and substr(to_char(mrm.make_date,'yyyyMMdd'),0,6) between #{accYearB}||''||#{accMonthB} and #{accYearE}||''||#{accMonthE} and mrm.state = 2

select mrm.req_code,
       hsd.store_name,
       mid.inv_code,
       mid.inv_name,
       mid.inv_model,
       hu.unit_name,
       nvl(aa.req_amount,0) amount,
       mid.plan_price price,
       nvl(aa.req_amount,0)*nvl(mid.plan_price,0) req_money
from med_require_main mrm 
left join hos_store_dict hsd on mrm.hos_id = hsd.hos_id and mrm.group_id = hsd.group_id and mrm.stock_no = hsd.store_no 
     and hsd.store_id = mrm.stock_id and hsd.is_stop = 0
left join (
     select group_id,hos_id,copy_code,total_id,inv_id,sum(nvl(req_amount,0)) req_amount
     from med_require_rela
     where group_id = #{group_id} 
     	and hos_id = #{hos_id} 
     	and copy_code = #{copy_code}  
     	and total_id = #{req_id}
     group by group_id,hos_id,copy_code,total_id,inv_id
) aa on aa.group_id = mrm.group_id and aa.hos_id = mrm.hos_id and aa.copy_code = mrm.copy_code and mrm.req_id = aa.total_id
left join med_inv_dict mid on mid.inv_id = aa.inv_id and mid.copy_code = aa.copy_code and mid.hos_id = aa.hos_id
     and mid.group_id = aa.group_id  and mid.is_stop = 0
left join hos_unit hu on hu.group_id = mid.group_id and hu.hos_id = mid.hos_id and hu.unit_code = mid.unit_code
     and hu.is_stop = 0
where mrm.hos_id = #{hos_id}
      and mrm.group_id = #{group_id}
      and mrm.copy_code = #{copy_code}
      and mrm.req_id = #{req_id}

with med_req_table as( SELECT mrm.group_id, mrm.hos_id, mrm.copy_code,mrm.req_id, mrm.state, mrm.dept_id, mrm.dept_no, mrm.stock_id, mrm.stock_no, mrd.inv_id, mrd.inv_no, mrd.price, nvl(mrd.amount, 0) AS amount, case when mrm.state=1 then nvl(mrd.amount, 0) else nvl(mrr.exec_amount, 0) end as exec_amount FROM med_require_main mrm LEFT JOIN med_require_detail mrd ON mrm.group_id = mrd.group_id AND mrm.hos_id = mrd.hos_id AND mrm.copy_code = mrd.copy_code AND mrm.req_id = mrd.req_id AND mrm.req_code = mrd.req_code LEFT JOIN (select req_id,inv_id,sum(exec_amount) as exec_amount from med_require_rela
and group_id = #{group_id,jdbcType=INTEGER}
AND hos_id = #{hos_id,jdbcType=INTEGER}
AND copy_code = #{copy_code,jdbcType=VARCHAR}
group by req_id,inv_id ) mrr ON mrm.req_id = mrr.req_id AND mrd.inv_id = mrr.inv_id
and mrm.group_id = #{group_id,jdbcType=INTEGER}
AND mrm.hos_id = #{hos_id,jdbcType=INTEGER}
AND mrm.copy_code = #{copy_code,jdbcType=VARCHAR}
AND mrm.stock_id = #{store_id,jdbcType=INTEGER}
AND mrm.make_date BETWEEN to_date(#{begin_date}, 'yyyy/MM/dd') AND to_date(#{end_date}, 'yyyy/MM/dd')
AND mrm.state in(1,3)
AND mrm.state = 3
AND mrm.state = 1
)
SELECT mrt.group_id , mrt.hos_id , mrt.copy_code , mrt.stock_id , mrt.stock_no , mrt.inv_id , mrt.inv_no , mid.inv_code , mid.inv_name , mid.inv_model , mid.unit_code , hu.unit_name , hsd.sup_name , nvl(mih.cur_amount, 0) AS cur_amount , nvl(mrt.amount, 0) AS req_amount , nvl(mrt.exec_amount, 0) AS exec_amount , mrt.price AS price , mrt.price * nvl(mrt.amount, 0) AS req_money , mrt.state AS state
, detail.detail_data AS detail_data
FROM (SELECT a.group_id , a.hos_id , a.copy_code , a.stock_id , a.stock_no , a.inv_id , a.inv_no , a.price , sum(nvl(a.amount, 0)) AS amount , sum(nvl(exec_amount, 0)) AS exec_amount , a.state AS state FROM med_req_table a group by a.group_id, a.hos_id, a.copy_code, a.stock_id, a.stock_no, a.inv_id, a.inv_no, a.price ,a.state ) mrt LEFT JOIN med_inv_dict mid ON mid.group_id = mrt.group_id AND mid.copy_code = mrt.copy_code AND mid.hos_id = mrt.hos_id AND mid.inv_id = mrt.inv_id AND mid.inv_no = mrt.inv_no AND mid.is_stop = 0 LEFT JOIN ( SELECT sup_id, inv_id, group_id, hos_id, copy_code,is_default FROM med_inv_sup WHERE is_default = 1 ) mis on mid.group_id = mis.group_id and mid.hos_id = mis.hos_id and mid.copy_code = mis.copy_code and mid.inv_id = mis.inv_id LEFT JOIN hos_sup_dict hsd ON mis.group_id = hsd.group_id AND mis.hos_id = hsd.hos_id AND mis.sup_id = hsd.sup_id AND hsd.is_stop = 0 LEFT JOIN hos_unit hu ON hu.group_id = mid.group_id AND hu.hos_id = mid.hos_id AND hu.unit_code = mid.unit_code AND hu.is_stop = 0 LEFT JOIN med_inv_hold mih ON mih.group_id = mrt.group_id AND mih.hos_id = mrt.hos_id AND mih.copy_code = mrt.copy_code AND mih.inv_id = mrt.inv_id AND mih.store_id = mrt.stock_id
LEFT JOIN ( SELECT a.inv_id, a.inv_no, SUM(nvl(a.amount, 0)) AS amount, '[' || to_char(WM_CONCAT('{"req_id":' || a.req_id || ',"inv_id":' || a.inv_id || ',"inv_no":' || a.inv_no || ',"exec_amount":' || a.amount || ',"req_amount":' || a.amount || '}')) || ']' AS detail_data FROM med_req_table a GROUP BY a.inv_id, a.inv_no ) detail ON mid.inv_id = detail.inv_id AND mid.inv_no = detail.inv_no
ORDER BY mid.inv_code ASC

SELECT mrm.group_id, mrm.hos_id, mrm.copy_code, mrm.req_id, mrm.req_code, mrm.dept_id, mrm.dept_no, hdd.dept_code, hdd.dept_name, mrd.inv_id, mrd.inv_no, mid.inv_code, mid.inv_name, mid.inv_model, mid.unit_code, hu.unit_name, nvl(mih.cur_amount,0) cur_amount,--当前库存 nvl(mrd.amount,0) req_amount,--需求数量 case when mrm.state=1 then nvl(mrd.amount, 0) else nvl(mrr.exec_amount, 0) end as exec_amount,--执行数量 mrd.price price,--价格 mrd.price * nvl(mrd.amount,0) req_money,--需求金额 mrm.state --状态 FROM med_require_main mrm LEFT JOIN med_require_detail mrd ON mrm.group_id = mrd.group_id AND mrm.hos_id = mrd.hos_id AND mrm.copy_code = mrd.copy_code AND mrm.req_id = mrd.req_id AND mrm.req_code = mrd.req_code LEFT JOIN (select req_id,inv_id,sum(exec_amount) as exec_amount from med_require_rela
AND group_id = #{group_id,jdbcType=INTEGER}
AND hos_id = #{hos_id,jdbcType=INTEGER}
AND copy_code = #{copy_code,jdbcType=VARCHAR}
group by req_id,inv_id ) mrr ON mrm.req_id = mrr.req_id AND mrd.inv_id = mrr.inv_id LEFT JOIN hos_dept_dict hdd ON hdd.dept_id = mrm.dept_id AND hdd.dept_no = mrm.dept_no AND hdd.group_id = mrm.group_id AND hdd.hos_id = mrm.hos_id AND hdd.is_stop = 0 LEFT JOIN med_inv_dict mid ON mid.group_id = mrd.group_id AND mid.copy_code = mrd.copy_code AND mid.hos_id = mrd.hos_id AND mid.inv_id = mrd.inv_id AND mid.inv_no = mrd.inv_no AND mid.is_stop = 0 LEFT JOIN hos_unit hu ON hu.group_id = mid.group_id AND hu.hos_id = mid.hos_id AND hu.unit_code = mid.unit_code AND hu.is_stop = 0 LEFT JOIN med_inv_hold mih ON mih.group_id = mrm.group_id AND mih.hos_id = mrm.hos_id AND mih.copy_code = mrm.copy_code AND mih.inv_id = mrd.inv_id AND mih.store_id = mrm.stock_id
and mrm.group_id = #{group_id,jdbcType=INTEGER}
AND mrm.hos_id = #{hos_id,jdbcType=INTEGER}
AND mrm.copy_code = #{copy_code,jdbcType=VARCHAR}
AND mrm.stock_id = #{store_id,jdbcType=INTEGER}
AND mrm.stock_no = #{store_no,jdbcType=INTEGER}
AND mrm.dept_id = #{dept_id,jdbcType=INTEGER}
AND mrm.dept_no = #{dept_no,jdbcType=INTEGER}
AND mrm.req_code like #{req_code,jdbcType=VARCHAR}||'%'
and( mid.inv_code like '%'||#{inv_code}||'%' or mid.inv_name like '%'||#{inv_code}||'%' or mid.spell_code like '%'||#{inv_code}||'%' or mid.wbx_code like '%'||#{inv_code}||'%' )
AND mrm.make_date BETWEEN to_date(#{begin_date}, 'yyyy/MM/dd') AND to_date(#{end_date}, 'yyyy/MM/dd') AND mrm.state = 1
order by mrm.req_code desc
