SELECT mcd.hos_id as hos_id, mcd.group_id as group_id, mcd.copy_code as copy_code, mid.bid_code as bid_code, mcd.check_id as check_id, hf.fac_name as fac_name, mcd.check_code as check_code, mcd.detail_id as detail_id, mcd.inv_id as inv_id, mcd.inv_no as inv_no, mcd.batch_no as batch_no, mcd.bar_code as bar_code, mcd.location_id as location_id, mcd.cur_amount as cur_amount, mcd.cur_amount*mcd.price as cur_money, mcd.chk_amount as chk_amount, mcd.chk_amount*mcd.price as chk_money, mcd.chk_amount - mcd.cur_amount as pl_amount, (mcd.chk_amount*mcd.price) - (mcd.cur_amount*mcd.price) as pl_money, mcd.price as price, mcd.inva_date as inva_date, mcd.disinfect_date as disinfect_date, mcd.note as note, mid.inv_code as inv_code, mid.inv_name as inv_name, mid.inv_model as inv_model, mid.unit_code as unit_code, hu.unit_name as unit_name, mld.location_code as location_new_code, mld.location_name as location_new_name, mfb.left_amount as left_amount FROM MED_CHECK_DETAIL mcd left join med_check_main mcm on mcd.group_id=mcm.group_id and mcd.hos_id=mcm.hos_id and mcd.copy_code=mcm.copy_code and mcd.check_id=mcm.check_id left join med_inv_dict mid on mcd.group_id = mid.group_id and mcd.hos_id = mid.hos_id and mcd.copy_code = mid.copy_code and mcd.inv_no = mid.inv_no and mcd.inv_id = mid.inv_id left join hos_fac_dict hf on hf.group_id = mid.group_id and hf.hos_id = mid.hos_id and hf.fac_id = mid.fac_id and hf.fac_no = mid.fac_no LEFT JOIN hos_unit hu ON mid.group_id = hu.group_id and mid.hos_id = hu.hos_id and mid.unit_code = hu.unit_code left join med_store_inv msi on mcd.group_id = msi.group_id and mcd.hos_id = msi.hos_id and mcd.copy_code = msi.copy_code and mcd.inv_id = msi.inv_id and msi.store_id = #{store_id,jdbcType=INTEGER} left join med_location_dict mld on msi.group_id = mld.group_id and msi.hos_id = mld.hos_id and msi.copy_code = mld.copy_code and msi.location_id = mld.location_id and mld.is_stop = 0 left join ( select group_id,hos_id,copy_code,inv_id,bar_code,batch_no,store_id,price,sum(left_amount) as left_amount from med_fifo_balance group by group_id,hos_id,copy_code,inv_id,bar_code,batch_no,store_id,price ) mfb on mcd.group_id = mfb.group_id and mcd.hos_id = mfb.hos_id and mcd.copy_code = mfb.copy_code and mcd.inv_id = mfb.inv_id and mcd.batch_no = mfb.batch_no and mcd.bar_code = mfb.bar_code and mcm.store_id = mfb.store_id and mcd.price = mfb.price
AND mcd.group_id = #{group_id,jdbcType=INTEGER}
AND mcd.hos_id = #{hos_id,jdbcType=INTEGER}
AND mcd.copy_code = #{copy_code,jdbcType=VARCHAR}
AND mcd.check_id = #{check_id,jdbcType=INTEGER}
order by ${sortname} ${sortorder}
order by mcd.detail_id asc

with w_fifo_temp as (
	select 
		group_id,
		hos_id,
        copy_code,
        left_amount,  --当前库存数量
        left_money,  --当前库存金额
        batch_sn,  --批次
        location_id
	from med_fifo_balance
	where group_id = #{group_id,jdbcType=INTEGER}
		AND hos_id = #{hos_id,jdbcType=INTEGER}
		AND copy_code = #{copy_code,jdbcType=VARCHAR}
		AND store_id = #{store_id,jdbcType=INTEGER}
		AND inv_id = #{inv_id,jdbcType=INTEGER}
		AND batch_no = #{batch_no,jdbcType=VARCHAR}
		AND bar_code = #{bar_code,jdbcType=VARCHAR}
),
w_out_temp as (
	select 
		a.inv_id,
        a.batch_sn,  --批次
        sum(a.amount) as imme_amount ,  --MED_FIFO_BALANCE库存数量 减 明细表出库未确认的库存
        sum(a.amount_money) as imme_amount_money
	from med_out_detail a
	left join med_out_main b 
		on a.group_id = b.group_id and a.hos_id = b.hos_id 
		and a.copy_code = b.copy_code and a.out_id = b.out_id
	where
		 b.state < 3 
		AND a.group_id = #{group_id,jdbcType=INTEGER}
		AND a.hos_id = #{hos_id,jdbcType=INTEGER}
		AND a.copy_code = #{copy_code,jdbcType=VARCHAR}
		AND b.store_id = #{store_id,jdbcType=INTEGER}
		AND a.inv_id = #{inv_id,jdbcType=INTEGER}
		AND a.batch_no = #{batch_no,jdbcType=VARCHAR}
		AND a.bar_code = #{bar_code,jdbcType=VARCHAR}
	group by a.inv_id, a.batch_sn
)
select 
	a.batch_sn,   --批次
	a.location_id,  --货位ID
	nvl(a.left_amount,0) - nvl(b.imme_amount,0) AS imme_amount,
	nvl(a.left_money,0) - nvl(b.imme_amount_money,0) AS imme_amount_money
from w_fifo_temp a
left join w_out_temp b 
	on a.batch_sn= b.batch_sn 
where 
	nvl(a.left_amount,0) - nvl(b.imme_amount,0) > 0
