mapper.insert:
INSERT INTO PACT_ASS_APPLY_FKHT ( group_id , hos_id , copy_code , pact_code , pact_det_id , apply_id , apply_det_id , pact_amount
) select t.* from(
select #{item.group_id,jdbcType=INTEGER} , #{item.hos_id,jdbcType=INTEGER} , #{item.copy_code,jdbcType=VARCHAR} , #{item.pact_code,jdbcType=VARCHAR} , #{item.pact_det_id,jdbcType=INTEGER} , #{item.apply_id,jdbcType=INTEGER} , #{item.apply_det_id,jdbcType=INTEGER} , #{item.pact_amount,jdbcType=INTEGER}
from dual
)t

mapper.update:

UPDATE PACT_ASS_APPLY_FKHT set
	pact_amount = #{pact_amount,jdbcType=INTEGER}
where                     
	group_id = #{group_id,jdbcType=INTEGER}
	AND hos_id = #{hos_id,jdbcType=INTEGER}
	AND copy_code = #{copy_code,jdbcType=VARCHAR}
	AND pact_code = #{pact_code,jdbcType=VARCHAR}
	AND pact_det_id = #{pact_det_id,jdbcType=INTEGER}
	AND apply_id = #{apply_id,jdbcType=VARCHAR}
	AND apply_det_id = #{apply_det_id,jdbcType=INTEGER}

update.foreach:
	UPDATE PACT_ASS_APPLY_FKHT set
	pact_amount = #{item.pact_amount,jdbcType=INTEGER}
