mapper.select:
SELECT group_id , hos_id , copy_code , pact_code , pact_det_id , apply_id , apply_det_id , pact_amount FROM PACT_ASS_APPLY_FKHT where group_id = #{group_id,jdbcType=INTEGER} AND hos_id = #{hos_id,jdbcType=INTEGER} AND copy_code = #{copy_code,jdbcType=VARCHAR}
AND pact_code = #{pact_code,jdbcType=VARCHAR}
order by pact_code asc


SELECT 
	group_id ,
	hos_id   ,
	copy_code ,
	pact_code ,
	pact_det_id ,
	apply_id ,
	apply_det_id ,
	pact_amount
FROM PACT_ASS_APPLY_FKHT 
WHERE 
	group_id = #{group_id,jdbcType=INTEGER}
	AND hos_id = #{hos_id,jdbcType=INTEGER}
	AND copy_code = #{copy_code,jdbcType=VARCHAR}
	AND pact_code = #{pact_code,jdbcType=VARCHAR}
	AND pact_det_id = #{pact_det_id,jdbcType=INTEGER}
	AND apply_id = #{apply_id,jdbcType=VARCHAR}
	AND apply_det_id = #{apply_det_id,jdbcType=INTEGER}


SELECT 
	group_id ,
	hos_id   ,
	copy_code ,
	pact_code ,
	pact_det_id ,
	apply_id ,
