SELECT
	pdf.PACT_CODE,pdf.DETAIL_ID,pdf.item_name,pdf.SUBJECT_TYPE,pdd.VALUE_NAME as subject_type_name,pdf.SUBJECT_ID,to_char(cast(temp.sub_name as varchar2(20))) as subject_name,pdf.SUBJECT_NO,pdf.ITEM_SPEC,pdf.ITEM_MODEL,pdf.PRICE,pdf.NOTE,pdf.UNIT_CODE, hu.UNIT_NAME,pdf.FAC_ID,hf.FAC_NAME
FROM
