mapper.select:
select a.group_id,a.hos_id,a.copy_code,a.inv_id subject_id,a.inv_code subject_code, a.inv_name subject_name,a.inv_model item_spec,a.plan_price price, hu.unit_name ,c.fac_id,c.fac_name from mat_inv_dict a left join hos_fac c on a.group_id=c.group_id and a.hos_id=c.hos_id and a.fac_id=c.fac_id and c.is_stop=0 left join mat_inv_sup mis on a.group_id = mis.group_id and a.hos_id = mis.hos_id and a.copy_code=mis.copy_code and a.inv_id=mis.inv_id and mis.is_default=1 left join hos_sup hs on mis.group_id=hs.group_id and mis.hos_id=hs.hos_id and mis.sup_id=hs.sup_id and hs.is_stop=0 left join MAT_STORE_INV dhc on a.group_id =dhc.group_id and a.hos_id = dhc.hos_id and a.copy_code= dhc.copy_code and a.inv_id = dhc.inv_id left join MAT_TYPE dhcc on a.group_id = dhcc.group_id and a.hos_id =dhcc.hos_id and a.copy_code = dhcc.copy_code and a.mat_type_id = dhcc.mat_type_id LEFT JOIN HOS_UNIT hu on a.GROUP_ID = hu.GROUP_ID AND a.HOS_ID = hu.HOS_ID AND a.unit_code = hu.UNIT_CODE where a.group_id = #{group_id,jdbcType=INTEGER}
and a.hos_id = #{hos_id,jdbcType=INTEGER}
and a.copy_code = #{copy_code,jdbcType=VARCHAR}
and hs.sup_id = #{sup_no,jdbcType=INTEGER}
and dhc.store_id = #{mat_store,jdbcType=INTEGER}
and dhcc.mat_type_id=#{inv_category,jdbcType=INTEGER}
order by a.inv_code
