mapper.insert:
INSERT INTO prm_emp_kpi_led ( group_id, hos_id, copy_code, sec_code, acc_year, acc_month, kpi_code, emp_id, emp_no, kpi_beg_score, kpi_end_score, goal_code
) select t.* from(
select #{item.group_id}, #{item.hos_id}, #{item.copy_code}, #{item.sec_code}, #{item.acc_year}, #{item.acc_month}, #{item.kpi_code}, #{item.emp_id}, #{item.emp_no}, #{item.kpi_beg_score}, #{item.kpi_end_score} , #{item.goal_code} from dual
)t

mapper.update:
UPDATE prm_emp_kpi_led SET sec_code = #{sec_code} , kpi_beg_score = #{kpi_beg_score} , kpi_end_score = #{kpi_end_score}
WHERE
group_id = #{group_id} and hos_id = #{hos_id} and copy_code = #{copy_code} and sec_code = #{sec_code} and acc_year = #{acc_year} and acc_month = #{acc_month} and goal_code = #{goal_code} and kpi_code = #{kpi_code} and emp_id = #{emp_id} and emp_no = #{emp_no}

mapper.delete:
DELETE FROM prm_emp_kpi_led WHERE


		group_id = #{item.group_id}


		 and  hos_id = #{item.hos_id}


		 and copy_code = #{item.copy_code}


		 and acc_year = #{item.acc_year}


		 and acc_month = #{item.acc_month}


		 and goal_code = #{item.goal_code}


		 and kpi_code = #{item.kpi_code}


		 and emp_no = #{item.emp_no}


		 and emp_id = #{item.emp_id}

mapper.select:

SELECT 
  a.group_id, 
  a.hos_id, 
  a.copy_code,
  a.sec_code, 
  b.sec_name, 
  a.acc_year, 
  a.acc_month,
  a.kpi_code, 
  a.goal_code,
  a.emp_id, 
  a.emp_no, 
  a.kpi_beg_score, 
  a.kpi_end_score, 
  b.led_path 
FROM prm_emp_kpi_led a
left join prm_led b on
a.group_id = b.group_id
and a.hos_id = b.hos_id
and a.copy_code = b.copy_code
and a.sec_code = b.sec_code


AND a.group_id = #{group_id}


AND a.hos_id = #{hos_id}


AND a.copy_code = #{copy_code}


AND a.sec_code = #{sec_code}


AND a.goal_code = #{goal_code}


AND a.acc_year = #{acc_year}


AND a.acc_month = #{acc_month}


AND a.kpi_code = #{kpi_code}


AND a.emp_id = #{emp_id}


AND b.sec_name = #{sec_name}


AND b.led_path = #{led_path}


order by a.sec_code asc
