mapper.insert:
INSERT INTO PRM_FUN_LINK ( group_id , hos_id , copy_code , fun_code , source_name , page_code , page_data , is_group
) select PRM_FUN_LINK_SEQ.nextval , t.* from(
select #{item.group_id} , #{item.hos_id} , #{item.copy_code} , #{item.fun_code} , #{item.source_name} , #{item.page_code} , #{item.page_data} , #{item.is_group}
)t

mapper.update:

UPDATE prm_fun_link SET
source_name = #{source_name}
,
page_code = #{page_code}
,
page_data = #{page_data}
,
is_group = #{is_group}

WHERE 
group_id = #{group_id}
 and 
hos_id = #{hos_id}
 and 
copy_code = #{copy_code}
 and 
fun_code = #{fun_code}

update.foreach:
UPDATE prm_fun_link SET
source_name = #{source_name}
,
page_code = #{page_code}
,
page_data = #{page_data}
,
is_group = #{is_group}

WHERE 
group_id = #{item.group_id}
 and 
hos_id = #{item.hos_id}
 and 
copy_code = #{item.copy_code}
 and 
fun_code = #{item.fun_code}

mapper.delete:
DELETE FROM prm_fun_link WHERE

		group_id = #{item.group_id}
		 and 
		hos_id = #{item.hos_id}
		 and 
		copy_code = #{item.copy_code}
		 and 
		fun_code = #{item.fun_code}

mapper.select:

SELECT 
group_id,
hos_id,
copy_code,
fun_code,
source_name,
page_code,
page_data,
is_group
FROM PRM_FUN_LINK 


AND group_id = #{group_id}


AND hos_id = #{hos_id}


AND copy_code = #{copy_code}


AND fun_code = #{fun_code}


AND source_name = #{source_name}


AND page_code = #{page_code}


AND page_data = #{page_data}


AND is_group = #{is_group}


order by group_id asc
