with o_tmp as ( select a.group_id,a.hos_id,a.copy_code,a.order_id,sum(b.amount) sum_order_amount from mat_order_main a left join mat_order_detail b on a.group_id=b.group_id and a.hos_id=b.hos_id and a.copy_code=b.copy_code and a.order_id=b.order_id left join mat_inv_dict c on b.group_id=c.group_id and b.hos_id=c.hos_id and b.copy_code=c.copy_code and b.inv_id=c.inv_id and b.inv_no=c.inv_no
and a.group_id = #{group_id,jdbcType=INTEGER}
and a.hos_id = #{hos_id,jdbcType=INTEGER}
and a.copy_code = #{copy_code,jdbcType=VARCHAR}
and a.state = #{state,jdbcType=INTEGER}
and b.is_closed=0 and c.is_com = 0
group by a.group_id,a.hos_id,a.copy_code,a.order_id ),o_d_tmp as ( select a.group_id,a.hos_id,a.copy_code,a.order_id,b.inv_id,sum(b.amount) order_amount from mat_order_main a left join mat_order_detail b on a.group_id=b.group_id and a.hos_id=b.hos_id and a.copy_code=b.copy_code and a.order_id=b.order_id left join mat_inv_dict c on b.group_id=c.group_id and b.hos_id=c.hos_id and b.copy_code=c.copy_code and b.inv_id=c.inv_id and b.inv_no=c.inv_no
and a.group_id = #{group_id,jdbcType=INTEGER}
and a.hos_id = #{hos_id,jdbcType=INTEGER}
and a.copy_code = #{copy_code,jdbcType=VARCHAR}
and a.state = #{state,jdbcType=INTEGER}
and b.is_closed=0 and c.is_com = 0
group by a.group_id,a.hos_id,a.copy_code,a.order_id,b.inv_id ),o_sum as ( select aa.group_id,aa.hos_id,aa.copy_code,aa.order_id,aa.sum_order_amount,bb.inv_id,bb.order_amount from o_tmp aa left join o_d_tmp bb on aa.group_id=bb.group_id and aa.hos_id=bb.hos_id and aa.copy_code=bb.copy_code and aa.order_id=bb.order_id ),d_sum as ( select distinct a.delivery_id,a.order_id,a.inv_id from sup_delivery_order_rela a left join mat_inv_dict c on a.group_id=c.group_id and a.hos_id=c.hos_id and a.copy_code=c.copy_code and a.inv_id=c.inv_id join o_sum b on a.group_id=b.group_id and a.hos_id=b.hos_id and a.copy_code=b.copy_code and a.order_id=b.order_id and a.inv_id=b.inv_id where c.is_stop=0 and c.is_com=0 ),i_sum as ( select n.group_id,n.hos_id,n.copy_code,n.inv_id,m.order_id,sum(n.amount) in_amount from ( select distinct b.group_id,b.hos_id,b.copy_code,b.in_id,a.inv_id,a.order_id from d_sum a join sup_delivery_in_rela b on a.delivery_id=b.delivery_id and a.inv_id=b.inv_id
and b.in_id=#{in_id,jdbcType=INTEGER}
and b.is_com=#{is_com,jdbcType=INTEGER}
) m left join mat_in_detail n on m.group_id=n.group_id and m.hos_id=n.hos_id and m.copy_code=n.copy_code and m.in_id=n.in_id and m.inv_id=n.inv_id group by n.group_id,n.hos_id,n.copy_code,n.inv_id,m.order_id ) select mm.group_id,mm.hos_id,mm.copy_code,mm.order_id,mm.inv_id, os.sum_order_amount,os.order_amount,mm.in_amount from i_sum mm left join o_sum os on mm.group_id=os.group_id and mm.hos_id=os.hos_id and mm.copy_code=os.copy_code and mm.order_id=os.order_id and mm.inv_id=os.inv_id where os.inv_id is not null

