mapper.select:
SELECT 
a.SER_NUM,a.DEPT_ID,a.DEPT_NO,b.dept_code ||' '||b.DEPT_NAME as DEPT_NAME,a.FT_BL,a.FT_MY,a.NOTE,a.COOP_OBJ 
FROM ACC_COOP_COST_DETAIL a
LEFT JOIN HOS_DEPT_DICT b ON a.GROUP_ID = b.GROUP_ID AND a.HOS_ID = b.HOS_ID AND a.DEPT_ID = b.DEPT_ID AND b.IS_STOP = 0
LEFT JOIN ACC_COOP_COST c ON a.GROUP_ID = c.GROUP_ID AND a.HOS_ID = c.HOS_ID AND a.COPY_CODE = c.COPY_CODE AND a.SER_NUM = c.SER_NUM

	a.GROUP_ID = #{group_id}
	AND a.HOS_ID = #{hos_id}
	AND a.COPY_CODE = #{copy_code}
	AND a.coop_obj = 0
	AND a.SER_NUM = #{ser_num}

		AND c.PROJ_CODE = #{proj_code}

SELECT a.SER_NUM, a.FT_BL, a.FT_MY, a.NOTE, CASE a.COOP_OBJ WHEN 1 THEN b.cus_code||' '||b.CUS_NAME ELSE d.sup_code||' '||d.SUP_NAME END AS obj_name, CASE a.COOP_OBJ WHEN 1 THEN '客户' ELSE '供应商' END AS coop_obj_name, a.CUS_ID, a.CUS_NO, a.SUP_ID, a.SUP_NO, a.COOP_OBJ FROM ACC_COOP_COST_DETAIL a LEFT JOIN HOS_CUS_DICT b on a.GROUP_ID = b.GROUP_ID AND a.HOS_ID = b.HOS_ID AND a.CUS_ID = b.CUS_ID AND b.IS_STOP = 0 LEFT JOIN HOS_SUP_DICT d on a.GROUP_ID = d.GROUP_ID AND a.HOS_ID = d.HOS_ID AND a.SUP_ID = d.SUP_ID AND d.IS_STOP = 0 LEFT JOIN ACC_COOP_COST c ON a.GROUP_ID = c.GROUP_ID AND a.HOS_ID = c.HOS_ID AND a.COPY_CODE = c.COPY_CODE AND a.SER_NUM = c.SER_NUM
a.GROUP_ID = #{group_id} AND a.HOS_ID = #{hos_id} AND a.COPY_CODE = #{copy_code} AND a.coop_obj != 0 AND a.SER_NUM = #{ser_num}
AND c.PROJ_CODE = #{proj_code}

delete.foreach:
	delete ACC_COOP_COST_DETAIL where
 	group_id = #{item.group_id}
	and hos_id = #{item.hos_id}
	and copy_code = #{item.copy_code}
	and ser_num = #{item.ser_num}

		and dept_id = #{item.dept_id}


		and cus_id = #{item.cus_id}


		and sup_id = #{item.sup_id}

mapper.delete:
	delete ACC_COOP_COST_DETAIL where
 	group_id = #{group_id}
	and hos_id = #{hos_id}
	and copy_code = #{copy_code}
	and ser_num = #{ser_num}
