) select t.* from(
select #{item.group_id,jdbcType=INTEGER} , #{item.hos_id,jdbcType=INTEGER} , #{item.copy_code,jdbcType=VARCHAR} , #{item.ass_card_no,jdbcType=VARCHAR} , #{item.accessory_code,jdbcType=VARCHAR} , #{item.accessory_name,jdbcType=VARCHAR} , #{item.accessory_amount,jdbcType=INTEGER} , #{item.accessory_price,jdbcType=INTEGER} , #{item.accessory_money,jdbcType=INTEGER} , #{item.naturs_code,jdbcType=VARCHAR} , #{item.accessory_card_no,jdbcType=VARCHAR} , #{item.spell_code,jdbcType=VARCHAR} , #{item.wbx_code,jdbcType=VARCHAR} , #{item.note,jdbcType=VARCHAR} , #{item.is_stop,jdbcType=INTEGER}
from dual
)t

