mapper.select:
SELECT a.group_id, a.hos_id, a.copy_code, a.dis_a_no, a.dispose_type,dt.dispose_type_name, a.note, a.create_emp,su2.user_name as create_emp_name, a.create_date, a.audit_emp,su3.user_name as audit_emp_name, a.apply_date, a.state, case when a.state = 0 then '新建' when a.state = 1 then '审核' when a.state = 2 then '申报确认' else '' end state_name FROM ASS_DISPOSAL_A_House a left join ass_dispose_type dt on a.dispose_type = dt.dispose_type_code left join sys_user su2 on a.group_id = su2.group_id and a.hos_id = su2.hos_id and a.create_emp = su2.user_id
left join sys_user su3 on a.group_id = su3.group_id and a.hos_id = su3.hos_id and a.audit_emp = su3.user_id
AND a.group_id = #{group_id,jdbcType=INTEGER} AND a.hos_id = #{hos_id,jdbcType=INTEGER} AND a.copy_code = #{copy_code,jdbcType=VARCHAR}
and exists( select 1 from ASS_DISPOSAL_A_DETAIL_House t left join ass_card_House t1 on t.group_id = t1.group_id and t.hos_id = t1.hos_id and t.copy_code = t1.copy_code and t.ass_card_no = t1.ass_card_no left join ass_no_dict af on af.group_id = t1.group_id and af.hos_id = t1.hos_id and af.copy_code = t1.copy_code and af.ass_id = t1.ass_id and af.is_stop = 0 left join hos_dept_dict dd on dd.group_id = t1.group_id and dd.hos_id = t1.hos_id and dd.dept_id = t1.dept_id and dd.dept_no = t1.dept_no
and a.group_id = t.group_id and a.hos_id = t.hos_id and a.copy_code = t.copy_code and a.dis_a_no = t.dis_a_no
AND t1.ass_id = #{ass_id}
AND t1.dept_id = #{dept_id}
and ( af.ass_code like '%${ass_ins}%' or af.ass_name like '%${ass_ins}%' or af.spell_code like '%${ass_ins}%' or af.wbx_code like '%${ass_ins}%' or t1.ass_spec like '%${ass_ins}%' or dd.dept_name like '%${ass_ins}%' )
)
AND a.dis_a_no = #{dis_a_no,jdbcType=VARCHAR}
AND a.dispose_type = #{dispose_type,jdbcType=INTEGER}
AND a.note = #{note,jdbcType=VARCHAR}
AND a.create_emp = #{create_emp,jdbcType=INTEGER}
AND a.create_date between to_date(#{create_date_beg},'YYYY-MM-DD') and to_date(#{create_date_end},'YYYY-MM-DD')
AND a.audit_emp = #{audit_emp,jdbcType=INTEGER}
AND a.apply_date between to_date(#{apply_date_beg},'YYYY-MM-DD') and to_date(#{apply_date_end},'YYYY-MM-DD')
AND a.state = #{state,jdbcType=INTEGER}
and not exists ( select 1 from ASS_DISPOSAL_AR_MAP_HOUSE b where a.group_id = b.group_id and a.hos_id = b.hos_id and a.copy_code= b.copy_code and a.dis_a_no = b.dis_a_no )
order by create_date desc


