mapper.insert:
INSERT INTO ASS_DISPOSAL_R_House ( group_id , hos_id , copy_code , dis_r_no , dispose_type , note , create_emp , create_date , audit_emp , apply_date , state
) select ASS_DISPOSAL_R_House_SEQ.nextval , t.* from(
select #{item.group_id,jdbcType=INTEGER} , #{item.hos_id,jdbcType=INTEGER} , #{item.copy_code,jdbcType=VARCHAR} , #{item.dis_r_no,jdbcType=VARCHAR} , #{item.dispose_type,jdbcType=INTEGER} , #{item.note,jdbcType=VARCHAR} , #{item.create_emp,jdbcType=INTEGER} , to_date(#{item.create_date},'YYYY-MM-DD') , #{item.audit_emp,jdbcType=INTEGER} , #{item.apply_date,jdbcType=DATE} , #{item.state,jdbcType=INTEGER}
from dual
)t

