mapper.select:
SELECT a.group_id, a.hos_id, a.copy_code, a.dis_r_no, a.dispose_type,dt.dispose_type_name, a.note, a.create_emp,su2.user_name as create_emp_name, a.create_date, a.audit_emp,su3.user_name as audit_emp_name, a.apply_date, a.state, case when a.state = 0 then '新建' when a.state = 1 then '审核' when a.state = 2 then '处置确认' else '' end state_name , nvl(aapm.is_import, 0) as is_import FROM ASS_DISPOSAL_R_House a left join ass_dispose_type dt on a.dispose_type = dt.dispose_type_code left join sys_user su2 on a.group_id = su2.group_id and a.hos_id = su2.hos_id and a.create_emp = su2.user_id
left join sys_user su3 on a.group_id = su3.group_id and a.hos_id = su3.hos_id and a.audit_emp = su3.user_id
left join ( select ap.group_id, ap.hos_id, ap.copy_code, ap.dis_r_no , case when count(1) > 0 then 1 else 0 end as is_import from ASS_DIS_A_R_MAP_House ap left join ASS_DISPOSAL_A_House b on ap.group_id = b.group_id and ap.hos_id = b.hos_id and ap.copy_code = b.copy_code and ap.ais_r_no = b.dis_a_no group by ap.group_id, ap.hos_id, ap.copy_code, ap.dis_r_no ) aapm on a.group_id = aapm.group_id and a.hos_id = aapm.hos_id and a.copy_code = aapm.copy_code and a.dis_r_no = aapm.dis_r_no
AND a.group_id = #{group_id,jdbcType=INTEGER} AND a.hos_id = #{hos_id,jdbcType=INTEGER} AND a.copy_code = #{copy_code,jdbcType=VARCHAR} and exists( select 1 from ASS_DISPOSAL_R_DETAIL_House t left join ass_card_House t1 on t.group_id = t1.group_id and t.hos_id = t1.hos_id and t.copy_code = t1.copy_code and t.ass_card_no = t1.ass_card_no left join ass_no_dict af on af.group_id = t1.group_id and af.hos_id = t1.hos_id and af.copy_code = t1.copy_code and af.ass_id = t1.ass_id and af.is_stop = 0 left join hos_dept_dict dd on dd.group_id = t1.group_id and dd.hos_id = t1.hos_id and dd.dept_id = t1.dept_id and dd.dept_no = t1.dept_no
and a.group_id = t.group_id and a.hos_id = t.hos_id and a.copy_code = t.copy_code and a.dis_r_no = t.dis_r_no
AND t1.ass_id = #{ass_id}
AND t1.dept_id = #{dept_id}
and ( af.ass_code like '%${ass_ins}%' or af.ass_name like '%${ass_ins}%' or af.spell_code like '%${ass_ins}%' or af.wbx_code like '%${ass_ins}%' or t1.ass_spec like '%${ass_ins}%' or dd.dept_name like '%${ass_ins}%' )
)
AND a.dis_r_no = #{dis_r_no,jdbcType=VARCHAR}
AND a.dispose_type = #{dispose_type,jdbcType=INTEGER}
AND a.note = #{note,jdbcType=VARCHAR}
AND a.create_emp = #{create_emp,jdbcType=INTEGER}
AND a.create_date between to_date(#{create_date_beg},'YYYY-MM-DD') and to_date(#{create_date_end},'YYYY-MM-DD')
AND a.audit_emp = #{audit_emp,jdbcType=INTEGER}
AND a.apply_date between to_date(#{apply_date_beg},'YYYY-MM-DD') and to_date(#{apply_date_end},'YYYY-MM-DD')
AND a.state = #{state,jdbcType=INTEGER}
order by a.create_date desc


SELECT 
group_id,
hos_id,
copy_code,
dis_r_no,
dispose_type,
note,
create_emp,
create_date,
audit_emp,
apply_date,
state
FROM ASS_DISPOSAL_R_House 


AND group_id = #{group_id,jdbcType=INTEGER}


AND hos_id = #{hos_id,jdbcType=INTEGER}


