mapper.select:
SELECT dis_r_no  FROM ASS_DISPOSAL_R_House 
WHERE
	group_id = #{group_id,jdbcType=INTEGER}
	AND hos_id = #{hos_id,jdbcType=INTEGER}
	AND copy_code = #{copy_code,jdbcType=VARCHAR}

	AND dis_r_no in  (${dis_r_no})



	and state != #{state,jdbcType=INTEGER}

with detail_temp as (select a.group_id, a.hos_id, a.copy_code, a.dis_r_no, a.dispose_type, c.dispose_type_name, a.note, a.create_emp, su2.user_name as create_emp_name, a.create_date, a.audit_emp, su3.user_name as audit_emp_name, a.apply_date, a.state, case when a.state = 0 then '新建' when a.state = 1 then '审核' when a.state = 2 then '处置确认' else '' end state_name, b.ass_card_no, g.ass_id, g.ass_no, g.ass_code, g.ass_name, b.price, b.now_depre, b.now_manage_depre, b.add_depre, b.add_manage_depre, b.cur_money, b.fore_money, b.dispose_cost, b.dispose_income, b.dispose_tax, e.dept_name from ASS_DISPOSAL_R_house a left join ASS_DISPOSAL_R_DETAIL_house b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.copy_code = b.copy_code and a.dis_r_no = b.dis_r_no left join ass_dispose_type c on a.dispose_type = c.dispose_type_code left join sys_user su2 on a.group_id = su2.group_id and a.hos_id = su2.hos_id and a.create_emp = su2.user_id
left join sys_user su3 on a.group_id = su3.group_id and a.hos_id = su3.hos_id and a.audit_emp = su3.user_id
left join ass_card_house d on d.group_id = b.group_id and d.hos_id = b.hos_id and d.copy_code = b.copy_code and d.ass_card_no = b.ass_card_no
left join hos_dept_dict e on d.group_id = e.group_id and d.hos_id = e.hos_id and d.dept_id = e.dept_id and e.is_stop = 0
left join ass_no_dict g on d.group_id = g.group_id and d.hos_id = g.hos_id and d.copy_code = g.copy_code and d.ass_id = g.ass_id and g.is_stop = 0
AND a.group_id = #{group_id,jdbcType=INTEGER} AND a.hos_id = #{hos_id,jdbcType=INTEGER} AND a.copy_code = #{copy_code,jdbcType=VARCHAR} and exists( select 1 from ASS_DISPOSAL_R_DETAIL_House t left join ass_card_House t1 on t.group_id = t1.group_id and t.hos_id = t1.hos_id and t.copy_code = t1.copy_code and t.ass_card_no = t1.ass_card_no left join ass_no_dict af on af.group_id = t1.group_id and af.hos_id = t1.hos_id and af.copy_code = t1.copy_code and af.ass_id = t1.ass_id and af.is_stop = 0 left join hos_dept_dict dd on dd.group_id = t1.group_id and dd.hos_id = t1.hos_id and dd.dept_id = t1.dept_id and dd.dept_no = t1.dept_no
and a.group_id = t.group_id and a.hos_id = t.hos_id and a.copy_code = t.copy_code and a.dis_r_no = t.dis_r_no
AND t1.ass_id = #{ass_id}
AND t1.ass_no = #{ass_no}
AND t1.dept_id = #{dept_id}
AND t1.dept_no = #{dept_no}
and ( af.ass_code like '%${ass_ins}%' or af.ass_name like '%${ass_ins}%' or af.spell_code like '%${ass_ins}%' or af.wbx_code like '%${ass_ins}%' or t1.ass_spec like '%${ass_ins}%' or dd.dept_name like '%${ass_ins}%' )
)
AND a.dis_r_no = #{dis_r_no,jdbcType=VARCHAR}
AND a.dispose_type = #{dispose_type,jdbcType=INTEGER}
AND a.note = #{note,jdbcType=VARCHAR}
AND a.create_emp = #{create_emp,jdbcType=INTEGER}
AND a.create_date between to_date(#{create_date_beg},'YYYY-MM-DD') and to_date(#{create_date_end},'YYYY-MM-DD')
AND a.audit_emp = #{audit_emp,jdbcType=INTEGER}
AND a.apply_date between to_date(#{apply_date_beg},'YYYY-MM-DD') and to_date(#{apply_date_end},'YYYY-MM-DD')
AND a.state = #{state,jdbcType=INTEGER}
), sum_temp as (select null group_id, null hos_id, null copy_code, null dis_r_no, null dispose_type, null dispose_type_name, '合计' note, null create_emp, null create_emp_name, null create_date, null audit_emp, null audit_emp_name, null apply_date, null state, null state_name, null ass_card_no, null ass_id, null ass_no, null ass_code, null ass_name, sum(price) price, sum(now_depre) now_depre, sum(now_manage_depre) now_manage_depre, sum(add_depre) add_depre, sum(add_manage_depre) add_manage_depre, sum(cur_money) cur_money, sum(fore_money) fore_money, sum(dispose_cost) dispose_cost, sum(dispose_income) dispose_income, sum(dispose_tax) dispose_tax,
null ass_spec, null ass_model, null dept_name from detail_temp
) select group_id, hos_id, copy_code, dis_r_no, dispose_type, dispose_type_name, note, create_emp, create_emp_name, create_date, audit_emp, audit_emp_name, apply_date, state, state_name, ass_card_no, ass_id, ass_no, ass_code, ass_name, price, now_depre, now_manage_depre, add_depre, add_manage_depre, cur_money, fore_money, dispose_cost, dispose_income, dispose_tax, dept_name from detail_temp union all select group_id, hos_id, copy_code, dis_r_no, dispose_type, dispose_type_name, note, create_emp, create_emp_name, create_date, audit_emp, audit_emp_name, apply_date, state, state_name, ass_card_no, ass_id, ass_no, ass_code, ass_name, price, now_depre, now_manage_depre, add_depre, add_manage_depre, cur_money, fore_money, dispose_cost, dispose_income, dispose_tax, dept_name from sum_temp order by create_date desc
