mapper.select:
select * from ( select card.ass_card_no, card.ass_naturs, ass.ass_code, ass.ass_name, typed.ass_type_name, card.ass_spec, card.ass_mondl, card.ass_brand, fac.fac_name, unit.unit_name, card.price, card.ass_amount, card.in_date, card.run_date, case when card.is_dept = 1 then '在用' else '在库' end is_dept, deptd.dept_name, sto.store_name, bus.bus_type_name, card.ass_seq_no, card.location, state.state_name, card.bar_code, card.depre_money, card.cur_money, gb.gb_name, gb.gb_code from v_ass_card card
left join ass_no_dict ass on card.ass_id = ass.ass_id and card.ass_no = ass.ass_no and card.group_id = ass.group_id and card.hos_id = ass.hos_id and card.copy_code = ass.copy_code
left join ass_type_dict typed on ass.ass_type_id = typed.ass_type_id and card.group_id = typed.group_id and card.hos_id = typed.hos_id and card.copy_code = typed.copy_code
left join hos_fac_dict fac on card.fac_id = fac.fac_id and card.fac_no = fac.fac_no and card.group_id = fac.group_id and card.hos_id = fac.hos_id
left join hos_unit unit on card.unit_code = unit.unit_code and card.group_id = unit.group_id and card.hos_id = unit.hos_id
left join hos_dept_dict deptd on card.dept_id = deptd.dept_id and card.group_id = deptd.group_id and card.hos_id = deptd.hos_id and deptd.is_stop = 0
left join hos_store_dict sto on card.store_id = sto.store_id and card.store_no = sto.store_no and card.group_id = sto.group_id and card.hos_id = sto.hos_id
left join ass_bus_type_dict bus on card.buy_type = bus.bus_type_code and card.group_id = bus.group_id and card.hos_id = bus.hos_id and card.copy_code = bus.copy_code
left join ass_card_use_state state on card.use_state = state.state_code
left join ass_gb_dict gb on card.gb_code = gb.gb_code
where card.group_id = #{group_id,jdbcType=INTEGER} and card.hos_id = #{hos_id,jdbcType=INTEGER} and card.copy_code = #{copy_code,jdbcType=VARCHAR} and card.is_dept = 1 and card.use_state != 7 and card.use_state != 6
and exists ( select 1 from v_user_data_perm b where card.group_id = b.group_id and card.hos_id = b.hos_id and to_char(card.dept_id) = b.perm_code and user_id = #{user_id,jdbcType=INTEGER} and TABLE_CODE = upper('HOS_DEPT_DICT') and (b.is_read = 1 or b.is_write = 1))
and card.price >= #{price_beg}
and card.price <= #{price_end}
and typed.ass_type_id = #{ass_type_id}
and (ass.ass_code like '%${ass_name}%' or ass.ass_name like '%${ass_name}%' or ass.spell_code like upper('%${ass_name}%') or ass.wbx_code like upper('%${ass_name}%'))
and deptd.dept_id = #{use_dept_id,jdbcType=INTEGER}
and card.store_id = #{store_id}
and card.use_state = #{use_state}
and card.ass_naturs = #{ass_naturs}
and card.is_measure = #{is_measure}
and card.in_date >= to_date(#{in_date_beg},'YYYYMMDD')
and card.in_date <= to_date(#{in_date_end},'YYYYMMDD')
and card.run_date >= to_date(#{run_date_beg},'YYYYMMDD')
and card.run_date <= to_date(#{run_date_end},'YYYYMMDD')
and card.ass_card_no = #{ass_card_no}
and ( card.bar_code = #{bar_code} or card.ASS_CARD_NO = #{bar_code} or card.ASS_ORI_CARD_NO = #{bar_code} )
union all
select '合计' ass_card_no, null ass_naturs, null ass_code, null ass_name, null ass_type_name, null ass_spec, null ass_mondl, null ass_brand, null fac_name, null unit_name, sum(card.price) price, sum(card.ass_amount) ass_amount, null in_date, null run_date, null is_dept, null dept_name, null store_name, null bus_type_name, null ass_seq_no, null location, null state_name, null bar_code, sum(card.depre_money) depre_money, sum(card.cur_money) cur_money, null gb_name, null gb_code from v_ass_card card
left join ass_no_dict ass on card.ass_id = ass.ass_id and card.ass_no = ass.ass_no and card.group_id = ass.group_id and card.hos_id = ass.hos_id and card.copy_code = ass.copy_code
left join ass_type_dict typed on ass.ass_type_id = typed.ass_type_id and card.group_id = typed.group_id and card.hos_id = typed.hos_id and card.copy_code = typed.copy_code
left join hos_fac_dict fac on card.fac_id = fac.fac_id and card.fac_no = fac.fac_no and card.group_id = fac.group_id and card.hos_id = fac.hos_id
left join hos_unit unit on card.unit_code = unit.unit_code and card.group_id = unit.group_id and card.hos_id = unit.hos_id
left join hos_dept_dict deptd on card.dept_id = deptd.dept_id and card.group_id = deptd.group_id and card.hos_id = deptd.hos_id and deptd.is_stop = 0
left join hos_store_dict sto on card.store_id = sto.store_id and card.store_no = sto.store_no and card.group_id = sto.group_id and card.hos_id = sto.hos_id
left join ass_bus_type_dict bus on card.buy_type = bus.bus_type_code and card.group_id = bus.group_id and card.hos_id = bus.hos_id and card.copy_code = bus.copy_code
left join ass_card_use_state state on card.use_state = state.state_code
where card.group_id = #{group_id,jdbcType=INTEGER} and card.hos_id = #{hos_id,jdbcType=INTEGER} and card.copy_code = #{copy_code,jdbcType=VARCHAR} and card.is_dept = 1 and card.use_state != 7 and card.use_state != 6
and exists ( select 1 from v_user_data_perm b where card.group_id = b.group_id and card.hos_id = b.hos_id and to_char(card.dept_id) = b.perm_code and user_id = #{user_id,jdbcType=INTEGER} and TABLE_CODE = upper('HOS_DEPT_DICT') and (b.is_read = 1 or b.is_write = 1))
and card.price >= #{price_beg}
and card.price <= #{price_end}
and typed.ass_type_id = #{ass_type_id}
and (ass.ass_code like '%${ass_name}%' or ass.ass_name like '%${ass_name}%' or ass.spell_code like upper('%${ass_name}%') or ass.wbx_code like upper('%${ass_name}%'))
and deptd.dept_id = #{use_dept_id,jdbcType=INTEGER}
and card.store_id = #{store_id}
and card.use_state = #{use_state}
and card.ass_naturs = #{ass_naturs}
and card.is_measure = #{is_measure}
and card.in_date >= to_date(#{in_date_beg},'YYYYMMDD')
and card.in_date <= to_date(#{in_date_end},'YYYYMMDD')
and card.run_date >= to_date(#{run_date_beg},'YYYYMMDD')
and card.run_date <= to_date(#{run_date_end},'YYYYMMDD')
and card.ass_card_no = #{ass_card_no}
and ( card.bar_code = #{bar_code} or card.ASS_CARD_NO = #{bar_code} or card.ASS_ORI_CARD_NO = #{bar_code} )
) t order by t.ass_card_no desc

