mapper.insert:
INSERT INTO ASS_REMOULD_A_SPECIAL ( group_id , hos_id , copy_code , apply_no , bus_type_code , ven_id , ven_no , create_emp , create_date , audit_emp , change_date , state , note
) select ASS_REMOULD_A_SPECIAL_SEQ.nextval , t.* from(
select #{item.group_id,jdbcType=INTEGER} , #{item.hos_id,jdbcType=INTEGER} , #{item.copy_code,jdbcType=VARCHAR} , #{item.apply_no,jdbcType=VARCHAR} , #{item.bus_type_code,jdbcType=VARCHAR} , #{item.ven_id,jdbcType=INTEGER} , #{item.ven_no,jdbcType=INTEGER} , #{item.create_emp,jdbcType=INTEGER} , #{item.create_date,jdbcType=DATE} , #{item.audit_emp,jdbcType=INTEGER} , #{item.change_date,jdbcType=DATE} , #{item.state,jdbcType=INTEGER} , #{item.note,jdbcType=VARCHAR}
from dual
)t

update.foreach:
UPDATE ass_remould_a_special 
set
	audit_emp = #{item.audit_emp,jdbcType=INTEGER}
	,
	change_date = to_date(#{item.change_date},'YYYY-MM-DD')
	,
	state = '2'
	where                     
	    group_id = #{item.group_id,jdbcType=INTEGER}
	AND hos_id = #{item.hos_id,jdbcType=INTEGER}
	AND copy_code = #{item.copy_code,jdbcType=VARCHAR}
	AND apply_no = #{item.apply_no,jdbcType=VARCHAR}

