mapper.select:
SELECT a.group_id, a.hos_id, a.copy_code, a.apply_no, a.bus_type_code, b.bus_type_name, a.ven_id, a.ven_no, c.sup_name, a.create_emp, d.user_name as create_emp_name, a.create_date, a.audit_emp, f.user_name as audit_emp_name, a.change_date, a.state, case when a.state = 0 then '新建' when a.state = 1 then '审核' when a.state = 2 then '确认' else '' end state_name, a.note FROM ASS_REMOULD_A_SPECIAL a left join ass_bus_type_dict b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.copy_code = b.copy_code and a.bus_type_code = b.bus_type_code left join hos_sup_dict c on a.group_id = c.group_id and a.hos_id =c.hos_id and a.ven_id = c.sup_id and a.ven_no = c.sup_no and c.is_stop = 0 and c.is_disable = 0 left join sys_user d on a.group_id = d.group_id and a.hos_id = d.hos_id and a.copy_code = d.copy_code and a.create_emp = d.user_id left join sys_user f on a.group_id = f.group_id and a.hos_id = f.hos_id and a.copy_code = f.copy_code and a.audit_emp = f.user_id where a.group_id = #{group_id,jdbcType=INTEGER} AND a.hos_id = #{hos_id,jdbcType=INTEGER} AND a.copy_code = #{copy_code,jdbcType=VARCHAR}
AND a.ven_id = #{ven_id,jdbcType=INTEGER}
AND a.ven_no = #{ven_no,jdbcType=INTEGER}
AND a.create_date between to_date(#{create_date_beg},'YYYY-MM-DD') and to_date(#{create_date_end},'YYYY-MM-DD')
AND a.state = #{state,jdbcType=INTEGER}
AND a.bus_type_code = #{bus_type_code,jdbcType=INTEGER}


SELECT 
group_id,
hos_id,
copy_code,
apply_no,
bus_type_code,
ven_id,
ven_no,
create_emp,
create_date,
audit_emp,
change_date,
state,
note
FROM ASS_REMOULD_A_SPECIAL 


AND group_id = #{group_id,jdbcType=INTEGER}


AND hos_id = #{hos_id,jdbcType=INTEGER}


AND copy_code = #{copy_code,jdbcType=VARCHAR}


AND apply_no = #{apply_no,jdbcType=VARCHAR}


AND bus_type_code = #{bus_type_code,jdbcType=VARCHAR}


AND ven_id = #{ven_id,jdbcType=INTEGER}


AND ven_no = #{ven_no,jdbcType=INTEGER}


AND create_emp = #{create_emp,jdbcType=INTEGER}


AND create_date = #{create_date,jdbcType=DATE}


AND audit_emp = #{audit_emp,jdbcType=INTEGER}


AND change_date = #{change_date,jdbcType=DATE}


AND state = #{state,jdbcType=INTEGER}


AND note = #{note,jdbcType=VARCHAR}


order by group_id asc


