mapper.insert:
INSERT INTO ASS_REMOULD_F_DETAIL_House ( group_id , hos_id , copy_code , fcord_no , ass_card_no , price , now_depre , now_manage_depre , add_depre , add_manage_depre , cur_money , fore_money , new_price , change_price , note
)
select to_number(#{item.group_id,jdbcType=INTEGER}) , to_number(#{item.hos_id,jdbcType=INTEGER}) , #{item.copy_code,jdbcType=VARCHAR} , #{item.fcord_no,jdbcType=VARCHAR} , #{item.ass_card_no,jdbcType=VARCHAR} , to_number(#{item.price,jdbcType=INTEGER}) , to_number(#{item.now_depre,jdbcType=INTEGER}) , to_number(#{item.now_manage_depre,jdbcType=INTEGER}) , to_number(#{item.add_depre,jdbcType=INTEGER}) , to_number(#{item.add_manage_depre,jdbcType=INTEGER}) , to_number(#{item.cur_money,jdbcType=INTEGER}) , to_number(#{item.fore_money,jdbcType=INTEGER}) , to_number(#{item.new_price,jdbcType=INTEGER}) , to_number(#{item.change_price,jdbcType=INTEGER}) , #{item.note,jdbcType=VARCHAR}
from dual

mapper.update:

UPDATE ass_remould_f_detail_House 


price = #{price,jdbcType=INTEGER}
,


now_depre = #{now_depre,jdbcType=INTEGER}
,


now_manage_depre = #{now_manage_depre,jdbcType=INTEGER}
,


add_depre = #{add_depre,jdbcType=INTEGER}
,


add_manage_depre = #{add_manage_depre,jdbcType=INTEGER}
,


cur_money = #{cur_money,jdbcType=INTEGER}
,


fore_money = #{fore_money,jdbcType=INTEGER}
,


new_price = #{new_price,jdbcType=INTEGER}
,


change_price = nvl(#{change_price,jdbcType=INTEGER},0)


note = #{note,jdbcType=VARCHAR}
,


where                     
	group_id = #{group_id,jdbcType=INTEGER}
AND hos_id = #{hos_id,jdbcType=INTEGER}
AND copy_code = #{copy_code,jdbcType=VARCHAR}
AND fcord_no = #{fcord_no,jdbcType=VARCHAR}
AND ass_card_no = #{ass_card_no,jdbcType=VARCHAR}


UPDATE ass_remould_f_detail_House 
set 
change_price = #{change_price,jdbcType=INTEGER}
where                     
	group_id = #{group_id,jdbcType=INTEGER}
AND hos_id = #{hos_id,jdbcType=INTEGER}
AND copy_code = #{copy_code,jdbcType=VARCHAR}
AND fcord_no = #{fcord_no,jdbcType=VARCHAR}
AND ass_card_no = #{ass_card_no,jdbcType=VARCHAR}

