			group_id = #{item.group_id,jdbcType=INTEGER}
		AND hos_id = #{item.hos_id,jdbcType=INTEGER}
		AND copy_code = #{item.copy_code,jdbcType=VARCHAR}
		AND fcord_no = #{item.fcord_no,jdbcType=VARCHAR}

