mapper.select:
SELECT a.group_id, a.hos_id, a.copy_code, a.record_no, a.bus_type_code, b.bus_type_name, a.ven_id, a.ven_no, c.sup_name, a.create_emp, d.user_name as create_emp_name, a.create_date, a.audit_emp, f.user_name as audit_emp_name, a.record_date, a.state, case when a.state = 0 then '新建' when a.state = 1 then '审核' when a.state = 2 then '申报确认' else '' end state_name,
a.note, nvl(aapm.is_import, 0) as is_import FROM ASS_REMOULD_R_other a
left join ass_bus_type_dict b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.copy_code = b.copy_code and a.bus_type_code = b.bus_type_code left join hos_sup_dict c on a.group_id = c.group_id and a.hos_id =c.hos_id and a.ven_id = c.sup_id and a.ven_no = c.sup_no and c.is_stop = 0 and c.is_disable = 0 left join sys_user d on a.group_id = d.group_id and a.hos_id = d.hos_id and a.copy_code = d.copy_code and a.create_emp = d.user_id left join sys_user f on a.group_id = f.group_id and a.hos_id = f.hos_id and a.copy_code = f.copy_code and a.audit_emp = f.user_id left join ( select ap.group_id, ap.hos_id, ap.copy_code, ap.record_no , case when count(1) > 0 then 1 else 0 end as is_import from ASS_REMOULD_A_R_MAP_other ap left join ASS_REMOULD_A_other b on ap.group_id = b.group_id and ap.hos_id = b.hos_id and ap.copy_code = b.copy_code and ap.apply_no = b.apply_no group by ap.group_id, ap.hos_id, ap.copy_code, ap.record_no ) aapm on a.group_id = aapm.group_id and a.hos_id = aapm.hos_id and a.copy_code = aapm.copy_code and a.record_no = aapm.record_no
AND a.group_id = #{group_id,jdbcType=INTEGER}
AND a.hos_id = #{hos_id,jdbcType=INTEGER}
AND a.copy_code = #{copy_code,jdbcType=VARCHAR}
AND a.record_no = #{record_no,jdbcType=VARCHAR}
AND a.bus_type_code = #{bus_type_code,jdbcType=VARCHAR}
AND a.ven_id = #{ven_id,jdbcType=INTEGER}
AND a.ven_no = #{ven_no,jdbcType=INTEGER}
AND a.create_emp = #{create_emp,jdbcType=INTEGER}
AND a.create_date between to_date(#{create_date_beg},'YYYY-MM-DD') and to_date(#{create_date_end},'YYYY-MM-DD')
AND a.audit_emp = #{audit_emp,jdbcType=INTEGER}
AND a.record_date = #{record_date,jdbcType=DATE}
AND a.state = #{state,jdbcType=INTEGER}
AND a.note = #{note,jdbcType=VARCHAR}
order by group_id asc


SELECT 
group_id,
hos_id,
copy_code,
record_no,
bus_type_code,
ven_id,
ven_no,
create_emp,
create_date,
audit_emp,
record_date,
state,
note
FROM ASS_REMOULD_R_Other 


AND group_id = #{group_id,jdbcType=INTEGER}


AND hos_id = #{hos_id,jdbcType=INTEGER}


AND copy_code = #{copy_code,jdbcType=VARCHAR}


AND record_no = #{record_no,jdbcType=VARCHAR}


AND bus_type_code = #{bus_type_code,jdbcType=VARCHAR}


AND ven_id = #{ven_id,jdbcType=INTEGER}


AND ven_no = #{ven_no,jdbcType=INTEGER}


AND create_emp = #{create_emp,jdbcType=INTEGER}


AND create_date = #{create_date,jdbcType=DATE}


AND audit_emp = #{audit_emp,jdbcType=INTEGER}


AND record_date = #{record_date,jdbcType=DATE}


AND state = #{state,jdbcType=INTEGER}


AND note = #{note,jdbcType=VARCHAR}


order by group_id asc

