mapper.insert:
INSERT INTO ASS_BACK_SOURCE_Other ( group_id , hos_id , copy_code , ass_back_no , ass_card_no , source_id , price , now_depre , now_manage_depre , add_depre , add_manage_depre , cur_money , fore_money , note
) select t.* from(
select #{item.group_id,jdbcType=INTEGER} , #{item.hos_id,jdbcType=INTEGER} , #{item.copy_code,jdbcType=VARCHAR} , #{item.ass_back_no,jdbcType=VARCHAR} , #{item.ass_card_no,jdbcType=VARCHAR} , #{item.source_id,jdbcType=INTEGER} , #{item.price,jdbcType=INTEGER} null price , #{item.now_depre,jdbcType=INTEGER} 0 now_depre , #{item.now_manage_depre,jdbcType=INTEGER} 0 now_manage_depre , #{item.add_depre,jdbcType=INTEGER} null add_depre , #{item.add_manage_depre,jdbcType=INTEGER} null add_manage_depre , #{item.cur_money,jdbcType=INTEGER} null cur_money , #{item.fore_money,jdbcType=INTEGER} null fore_money , #{item.note,jdbcType=INTEGER} null note
from dual
)t

