mapper.insert:
INSERT INTO ASS_BILL_STAGE ( group_id , hos_id , copy_code , bill_no , naturs_code , ass_card_no , stage_no , stage_name , pay_plan_money
) select t.* from(
select #{item.group_id} , #{item.hos_id} , #{item.copy_code} , #{item.bill_no} , #{item.naturs_code} , #{item.ass_card_no} , #{item.stage_no} , #{item.stage_name} , #{item.pay_plan_money}
from dual
)t

mapper.update:

UPDATE ass_bill_stage 


stage_name = #{stage_name}
,


pay_plan_money = #{pay_plan_money}
,




AND group_id = #{group_id}


AND hos_id = #{hos_id}


AND copy_code = #{copy_code}


AND bill_no = #{bill_no}


AND naturs_code = #{naturs_code}


AND ass_card_no = #{ass_card_no}


AND stage_no = #{stage_no}

update.foreach:
UPDATE ass_bill_stage 


stage_name = #{item.stage_name}
,


pay_plan_money = #{item.pay_plan_money}
,




AND group_id = #{item.group_id}


AND hos_id = #{item.hos_id}


AND copy_code = #{item.copy_code}


AND bill_no = #{item.bill_no}


AND naturs_code = #{item.naturs_code}


AND ass_card_no = #{item.ass_card_no}


AND stage_no = #{item.stage_no}

