mapper.select:
SELECT t1.group_id , t1.hos_id , t1.copy_code , t1.bill_no , t1.naturs_code , t1.ass_card_no , t1.stage_no , t1.stage_name , t1.pay_plan_money
, t2.pay_plan_money plan_money , t2.pay_money , t2.unpay_money
FROM ASS_BILL_STAGE t1
left join ASS_PAY_STAGE_SPECIAL t2 on t1.group_id = t2.group_id and t1.hos_id = t2.hos_id and t1.copy_code = t2.copy_code and t1.ass_card_no = t2.ass_card_no and t1.stage_no = t2.stage_no
left join ASS_PAY_STAGE_GENERAL t2 on t1.group_id = t2.group_id and t1.hos_id = t2.hos_id and t1.copy_code = t2.copy_code and t1.ass_card_no = t2.ass_card_no and t1.stage_no = t2.stage_no
left join ASS_PAY_STAGE_HOUSE t2 on t1.group_id = t2.group_id and t1.hos_id = t2.hos_id and t1.copy_code = t2.copy_code and t1.ass_card_no = t2.ass_card_no and t1.stage_no = t2.stage_no
left join ASS_PAY_STAGE_OTHER t2 on t1.group_id = t2.group_id and t1.hos_id = t2.hos_id and t1.copy_code = t2.copy_code and t1.ass_card_no = t2.ass_card_no and t1.stage_no = t2.stage_no
left join ASS_PAY_STAGE_INASSETS t2 on t1.group_id = t2.group_id and t1.hos_id = t2.hos_id and t1.copy_code = t2.copy_code and t1.ass_card_no = t2.ass_card_no and t1.stage_no = t2.stage_no
left join ASS_PAY_STAGE_LAND t2 on t1.group_id = t2.group_id and t1.hos_id = t2.hos_id and t1.copy_code = t2.copy_code and t1.ass_card_no = t2.ass_card_no and t1.stage_no = t2.stage_no
AND t1.group_id = #{group_id}
AND t1.hos_id = #{hos_id}
AND t1.copy_code = #{copy_code}
AND t1.bill_no = #{bill_no}
AND t1.naturs_code = #{naturs_code}
AND t1.ass_card_no = #{ass_card_no}
AND t1.stage_no = #{stage_no}
order by t1.stage_no asc

SELECT t1.group_id , t1.hos_id , t1.copy_code , t1.bill_no , t1.naturs_code , t1.ass_card_no , t1.stage_no , t1.stage_name , t1.pay_plan_money , t2.pay_plan_money plan_money , t2.pay_money , t2.unpay_money , t2.bill_money , t2.unbill_money FROM ASS_BILL_STAGE t1
left join v_ass_pay_stage t2 on t1.group_id = t2.group_id and t1.hos_id = t2.hos_id and t1.copy_code = t2.copy_code and t1.ass_card_no = t2.ass_card_no and t1.stage_no = t2.stage_no and t1.naturs_code = t2.ass_naturs
left join ass_bill_main t3 on t1.group_id = t3.group_id and t1.hos_id = t3.hos_id and t1.copy_code = t3.copy_code and t1.bill_no = t3.bill_no
AND t1.group_id = #{group_id}
AND t1.hos_id = #{hos_id}
AND t1.copy_code = #{copy_code}
AND t1.bill_no = #{bill_no}
AND t1.naturs_code = #{naturs_code}
AND t1.ass_card_no = #{ass_card_no}
AND t1.stage_no = #{stage_no}
and t2.unbill_money > 0 and t3.state = 0
order by t1.stage_no asc


		SELECT 
			a.group_id,
			a.hos_id,
			a.copy_code,
			a.bill_no,
			a.naturs_code,
			a.ass_card_no,
			c.ass_code||''||c.ass_name ass_name,
			a.stage_no,
			a.stage_name,
			a.pay_plan_money,
			card.ass_mondl as ass_model,
			card.ass_spec,
			na.naturs_name
		FROM ASS_BILL_STAGE a
		left join (
			select group_id,hos_id,copy_code,ass_card_no,ass_id,ass_no,ass_mondl,ass_spec,'03' as  ass_naturs from ass_card_general
				where   group_id = #{group_id}
						AND hos_id = #{hos_id}
						AND copy_code = #{copy_code}
			union all
			select group_id,hos_id,copy_code,ass_card_no,ass_id,ass_no,ass_mondl,ass_spec,'02' as  ass_naturs from ass_card_special
				where   group_id = #{group_id}
						AND hos_id = #{hos_id}
						AND copy_code = #{copy_code}
			union all
			select group_id,hos_id,copy_code,ass_card_no,ass_id,ass_no,null,null,'01' as  ass_naturs from ass_card_house
				where   group_id = #{group_id}
						AND hos_id = #{hos_id}
						AND copy_code = #{copy_code}
			union all
			select group_id,hos_id,copy_code,ass_card_no,ass_id,ass_no,null,null,'05' as  ass_naturs from ass_card_inassets
				where   group_id = #{group_id}
						AND hos_id = #{hos_id}
						AND copy_code = #{copy_code}
			union all
			select group_id,hos_id,copy_code,ass_card_no,ass_id,ass_no,null,null,'06' as  ass_naturs from ass_card_land
				where   group_id = #{group_id}
						AND hos_id = #{hos_id}
						AND copy_code = #{copy_code}
			union all
			select group_id,hos_id,copy_code,ass_card_no,ass_id,ass_no,ass_mondl,ass_spec,'04' as  ass_naturs from ass_card_other
				where   group_id = #{group_id}
						AND hos_id = #{hos_id}
						AND copy_code = #{copy_code}
		) card on
		a.group_id = card.group_id
		and a.hos_id = card.hos_id
		and a.copy_code = card.copy_code
		and a.ass_card_no = card.ass_card_no
		left join ass_no_dict c on
		card.group_id = c.group_id
		and card.hos_id = c.hos_id
		and card.copy_code = c.copy_code
		and card.ass_id = c.ass_id
		and c.is_stop = 0
		 left join ass_naturs na
   		 on na.naturs_code = card.ass_naturs

		AND a.group_id = #{group_id}
		AND a.hos_id = #{hos_id}
		AND a.copy_code = #{copy_code}
		AND a.bill_no = #{bill_no}

		order by a.stage_no asc


SELECT 
group_id,
hos_id,
copy_code,
bill_no,
naturs_code,
ass_card_no,
stage_no,
stage_name,
pay_plan_money
FROM ASS_BILL_STAGE 

AND group_id = #{group_id}
AND hos_id = #{hos_id}
AND copy_code = #{copy_code}
AND bill_no = #{bill_no}
AND naturs_code = #{naturs_code}
AND ass_card_no = #{ass_card_no}

select 
	nvl(max(stage_no),1) max_stage_no 
from 
ASS_BILL_STAGE 

AND group_id = #{group_id}
AND hos_id = #{hos_id}
AND copy_code = #{copy_code}
AND bill_no = #{bill_no}
and naturs_code = #{naturs_code}
AND ass_card_no = #{ass_card_no}


SELECT 
group_id,
hos_id,
copy_code,
bill_no,
naturs_code,
ass_card_no,
stage_no,
stage_name,
pay_plan_money
FROM ASS_BILL_STAGE 


AND group_id = #{group_id}


AND hos_id = #{hos_id}


AND copy_code = #{copy_code}


AND bill_no = #{bill_no}


AND naturs_code = #{naturs_code}


AND ass_card_no = #{ass_card_no}


AND stage_no = #{stage_no}


AND stage_name = #{stage_name}


AND pay_plan_money = #{pay_plan_money}


order by group_id asc


SELECT 
group_id,
hos_id,
copy_code,
bill_no,
naturs_code,
ass_card_no,
stage_no,
stage_name,
pay_plan_money
FROM ASS_BILL_STAGE 


AND group_id = #{group_id}


AND hos_id = #{hos_id}


AND copy_code = #{copy_code}


AND bill_no = #{bill_no}


AND naturs_code = #{naturs_code}


AND ass_card_no = #{ass_card_no}


AND stage_no = #{stage_no}


AND stage_name = #{stage_name}


AND pay_plan_money = #{pay_plan_money}


order by group_id asc
