mapper.insert:
INSERT INTO ASS_PRE_BILL_MAIN ( group_id , hos_id , copy_code , bill_no , invoice_no , bill_date , ven_id , pact_code , bill_money , state , create_emp , create_date , audit_emp , audit_date , note
) select ASS_PRE_BILL_MAIN_SEQ.nextval , t.* from(
select #{item.group_id,jdbcType=INTEGER} , #{item.hos_id,jdbcType=INTEGER} , #{item.copy_code,jdbcType=VARCHAR} , #{item.bill_no,jdbcType=VARCHAR} , #{item.invoice_no,jdbcType=VARCHAR} , to_date(#{item.bill_date},'yyyy-MM-dd') , #{item.ven_id,jdbcType=VARCHAR} , #{item.pact_code,jdbcType=VARCHAR} , #{item.bill_money,jdbcType=INTEGER} , #{item.state,jdbcType=INTEGER} , #{item.create_emp,jdbcType=VARCHAR} , to_date(#{item.create_date},'yyyy-MM-dd') , #{item.audit_emp,jdbcType=VARCHAR} , to_date(#{item.audit_date,'yyyy-MM-dd') , #{item.note,jdbcType=VARCHAR}
from dual
)t

