mapper.select:
SELECT a.group_id, a.hos_id, a.copy_code, a.bill_no, a.invoice_no, a.bill_date, a.ven_id, a.ven_no, b.sup_name, a.pact_code, a.bill_money, a.state, a.create_emp, d.user_name as create_name, a.create_date, a.audit_emp, f.user_name as audit_name, a.audit_date, a.note FROM ASS_PRE_BILL_MAIN a left join HOS_SUP_DICT b on a.group_id =b.group_id and a.hos_id = b.hos_id and a.ven_id = b.sup_id and b.is_stop = 0 and b.is_disable= 0 left join sys_user d on a.group_id = d.group_id and a.hos_id = d.hos_id and a.create_emp = d.user_id and d.is_stop=0 left join sys_user f on a.group_id = f.group_id and a.hos_id = f.hos_id and a.audit_emp = f.user_id and f.is_stop=0
where a.group_id = #{group_id,jdbcType=INTEGER} AND a.hos_id = #{hos_id,jdbcType=INTEGER} AND a.copy_code = #{copy_code,jdbcType=VARCHAR}
AND a.bill_no = #{bill_no,jdbcType=VARCHAR}
AND a.invoice_no = #{invoice_no,jdbcType=VARCHAR}
AND a.bill_date between to_date(#{begin_bill_date},'yyyy-MM-dd') and to_date(#{end_bill_date} ,'yyyy-MM-dd')


SELECT 
group_id,
hos_id,
copy_code,
bill_no,
invoice_no,
bill_date,
ven_id,
pact_code,
bill_money,
state,
create_emp,
create_date,
audit_emp,
audit_date,
note
FROM ASS_PRE_BILL_MAIN 


AND group_id = #{group_id,jdbcType=INTEGER}


AND hos_id = #{hos_id,jdbcType=INTEGER}


AND copy_code = #{copy_code,jdbcType=VARCHAR}


AND bill_no = #{bill_no,jdbcType=VARCHAR}


AND invoice_no = #{invoice_no,jdbcType=VARCHAR}


AND bill_date = #{bill_date,jdbcType=DATE}


AND ven_id = #{ven_id,jdbcType=VARCHAR}


AND pact_code = #{pact_code}


AND bill_money = #{bill_money,jdbcType=INTEGER}


AND state = #{state,jdbcType=INTEGER}


AND create_emp = #{create_emp,jdbcType=VARCHAR}


AND create_date = #{create_date,jdbcType=DATE}


AND audit_emp = #{audit_emp,jdbcType=VARCHAR}


AND audit_date = #{audit_date,jdbcType=DATE}


AND note = #{note,jdbcType=VARCHAR}


order by group_id asc

		select a.bill_no as id,a.hos_id,a.copy_code,a.group_id, a.note as INIT_NOTE,d.ASS_NO,s.NATURS_CODE,s.naturs_name

		from ASS_PRE_BILL_DETAIL a
		left join ASS_NO_DICT d on  d.hos_id=a.hos_id and d.copy_code =a.copy_code and d.group_id =a.group_id and d.IS_STOP =0 and d.ass_id =a.ass_id 
   		 left join ASS_NATURS s on s.naturs_code = a.naturs_code

		where                     
			a.group_id = #{group_id,jdbcType=INTEGER}
			AND a.hos_id = #{hos_id,jdbcType=INTEGER}
			AND a.copy_code = #{copy_code,jdbcType=VARCHAR}


			AND a.bill_no = #{bill_no,jdbcType=VARCHAR}

SELECT bill_no  FROM ASS_PRE_BILL_MAIN 
	WHERE
		group_id = #{group_id,jdbcType=INTEGER}
		AND hos_id = #{hos_id,jdbcType=INTEGER}
		AND copy_code = #{copy_code,jdbcType=VARCHAR}

		AND bill_no in  (${paraId})


		AND bill_no = #{bill_no,jdbcType=VARCHAR}


		and state != #{state,jdbcType=INTEGER}
