AND group_id = #{group_id}
AND hos_id = #{hos_id}
AND copy_code = #{copy_code}
AND ass_card_no = #{ass_card_no}

DELETE FROM ass_card_Other 


		group_id = #{item.group_id}
		AND hos_id = #{item.hos_id}
		AND copy_code = #{item.copy_code}
		AND ass_card_no = #{item.ass_card_no}

DELETE FROM ass_card_Other 


		group_id = #{item.group_id}
		AND hos_id = #{item.hos_id}
		AND copy_code = #{item.copy_code}
		AND ass_in_no = #{item.ass_in_no}

		AND ass_id = #{item.ass_id}


		AND ass_no = #{item.ass_no}

mapper.select:
select count(1) from ASS_CARD_Other 
   where 	group_id = #{group_id}
		AND hos_id = #{hos_id}
		AND copy_code = #{copy_code}
		AND ass_in_no = #{ass_in_no}

		AND ass_id = #{ass_id}


		AND ass_no = #{ass_no}


		AND ass_spec = #{ass_spec}


		AND ass_mondl = #{ass_mondl}

select * from ( SELECT a.group_id, a.hos_id, a.copy_code, a.ass_card_no, a.ass_ori_card_no, a.ass_id, a.ass_no, ad.ass_code ass_id_code, ad.ass_name ass_id_name, a.gb_code, a.gb_code||' '||agd.gb_name gb_code_name, a.ass_spec, a.ass_mondl, a.ass_brand, a.ass_amount, a.unit_code, hu.unit_name unit_code_name, a.fac_no, a.fac_id, hfd.fac_code fac_id_code, hfd.fac_name fac_id_name, a.ven_id, a.ven_no, hsd.sup_code ven_id_code, hsd.sup_name ven_id_name, a.is_dept, case when a.is_dept = 0 then '' when a.is_dept = 1 then '' else '' end is_dept_name, a.dept_id, a.dept_no, hdd.dept_code dept_id_code, hdd.dept_name dept_id_name, a.store_id, a.store_no, htd.store_code store_id_code, htd.store_name store_id_name, a.price, a.depre_money, a.manage_depre_money, a.cur_money, a.fore_money, a.buy_type, abtd.bus_type_name buy_type_name, a.use_state, acus.state_name use_state_name, a.is_depr, case when a.is_depr = 0 then '' when a.is_depr = 1 then '' else '' end is_depr_name, a.depr_method, admd1.ass_depre_name depr_method_name, a.acc_depre_amount, a.is_manage_depre, case when a.is_manage_depre = 0 then '' when a.is_manage_depre = 1 then '' else '' end is_manage_depre_name, a.manage_depr_method, admd2.ass_depre_name manage_depr_method_name, a.manage_depre_amount, a.is_measure, case when a.is_measure = 0 then '' when a.is_measure = 1 then '' else '' end is_measure_name, a.is_throw, case when a.is_throw = 0 then '' when a.is_throw = 1 then '' else '' end is_throw_name, a.pact_code, a.contract_code, a.file_number, a.ins_money, a.ins_date, a.accept_emp, su.emp_name accept_emp_name, a.accept_date, a.service_date, a.ass_seq_no, a.location, a.note, a.ass_in_no, a.in_date, a.run_date, a.is_bar, case when a.is_bar = 0 then '' when a.is_bar = 1 then '' else '' end is_bar_name, a.bar_type, case when a.bar_type = 1 then '' when a.bar_type = 2 then '' else '' end bar_type_name, a.bar_code, a.bar_url, a.book_amount, a.relic_amount, a.relic_grade_code, a.is_init, case when a.is_init = 0 then '' when a.is_init = 1 then '' else '' end is_init_name, a.dispose_type, a.adt.dispose_type_name dispose_type_name, a.dispose_cost, a.dispose_income, a.dispose_tax, a.proj_id, a.proj_no, hpd.proj_code proj_id_code, hpd.proj_name proj_id_name, a.ass_purpose, aud.equi_usage_name ass_purpose_name, a.dispose_date, a.apply_date, a.add_depre_month, a.add_manage_month, a.reg_no, cert.cert_inv_name reg_no_name, a.is_bar_print, case when a.is_bar_print = 0 then '' when a.is_bar_print = 1 then '' else '' end is_bar_print_name, a.measure_type, case when a.measure_type = 0 then 'A' when a.measure_type = 1 then 'B' when a.measure_type = 2 then 'C' else '' end measure_type_name, a.measure_king_code, amkd.measure_king_name measure_king_code_name, a.simple_name, a.production_date, a.move_date, a.proc_store_id, a.proc_store_no, htd_proc.store_code proc_store_id_code, htd_proc.store_name proc_store_id_name, a.common_name, a.six_type_code, ats.type_name six_type_code_name FROM ASS_CARD_Other a
left join ass_no_dict ad on a.group_id = ad.group_id and a.hos_id = ad.hos_id and a.copy_code = ad.copy_code and a.ass_id = ad.ass_id and ad.is_stop = 0
left join ass_type_dict atd on ad.group_id = atd.group_id and ad.hos_id = atd.hos_id and ad.copy_code = atd.copy_code and ad.ass_type_id = atd.ass_type_id and atd.is_stop = 0
left join hos_unit hu on a.group_id = hu.group_id and a.hos_id = hu.hos_id and a.unit_code = hu.unit_code
left join hos_fac_dict hfd on a.group_id = hfd.group_id and a.hos_id = hfd.hos_id and a.fac_id = hfd.fac_id and hfd.is_stop = 0
left join hos_sup_dict hsd on a.group_id = hsd.group_id and a.hos_id = hsd.hos_id and a.ven_id = hsd.sup_id and hsd.is_stop = 0
left join hos_dept_dict hdd on a.group_id = hdd.group_id and a.hos_id = hdd.hos_id and a.dept_id = hdd.dept_id and hdd.is_stop = 0
left join hos_store_dict htd on a.group_id = htd.group_id and a.hos_id = htd.hos_id and a.store_id = htd.store_id and htd.is_stop = 0
left join ass_bus_type_dict abtd on a.group_id = abtd.group_id and a.hos_id = abtd.hos_id and a.copy_code = abtd.copy_code and a.buy_type = abtd.bus_type_code and abtd.is_stop = 0
left join ass_card_use_state acus on a.use_state = acus.state_code and acus.is_stop = 0
left join ass_depre_method_dict admd1 on a.depr_method = admd1.ass_depre_code and admd1.is_stop = 0
left join ass_depre_method_dict admd2 on a.manage_depr_method = admd2.ass_depre_code and admd2.is_stop = 0
left join ass_dispose_type adt on a.dispose_type = adt.dispose_type_code and adt.is_stop = 0
left join hos_emp_dict su on a.group_id = su.group_id and a.hos_id = su.hos_id and a.accept_emp = su.emp_id and su.is_stop = 0
left join hos_proj_dict hpd on a.group_id = hpd.group_id and a.hos_id = hpd.hos_id and a.proj_id = hpd.proj_id and hpd.is_stop = 0
left join ass_usage_dict aud on a.group_id = aud.group_id and a.hos_id = aud.hos_id and a.copy_code = aud.copy_code and a.ass_purpose = aud.equi_usage_code
left join ass_gb_dict agd on a.gb_code = agd.gb_code
left join ass_measure_king_dict amkd on a.group_id = amkd.group_id and a.hos_id = amkd.hos_id and a.copy_code = amkd.copy_code and a.measure_king_code = amkd.measure_king_code
left join ASS_CERT_MAIN cert on a.group_id = cert.group_id and a.hos_id = cert.hos_id and a.copy_code = cert.copy_code and a.reg_no = cert.cert_code
left join hos_store_dict htd_proc on a.group_id = htd_proc.group_id and a.hos_id = htd_proc.hos_id and a.proc_store_id = htd_proc.store_id and htd_proc.is_stop = 0
left join ASS_TYPE_SIXEIGHT ats on a.group_id = ats.group_id and a.hos_id = ats.hos_id and a.copy_code = ats.copy_code and a.six_type_code = ats.type_code
AND a.group_id = #{group_id} AND a.hos_id = #{hos_id} AND a.copy_code = #{copy_code}
and exists ( select 1 from v_user_data_perm b where a.group_id = b.group_id and a.hos_id = b.hos_id and to_char(a.dept_id) = b.perm_code and user_id = #{user_id,jdbcType= INTEGER} and TABLE_CODE = upper('HOS_DEPT_DICT') and (b.is_read = 1 or b.is_write = 1) ) and exists ( select 1 from v_user_data_perm b where a.group_id=b.group_id and a.hos_id=b.hos_id and to_char(ad.ass_type_id) = b.perm_code and user_id=#{user_id} AND TABLE_CODE = upper('ass_type_dict') and b.is_write = 1
union all
select 1 from v_user_data_perm b where a.group_id=b.group_id and a.hos_id=b.hos_id and to_char(ad.ass_type_id) = b.perm_code and user_id=#{user_id} AND TABLE_CODE = upper('ass_type_dict') and b.is_read = 1 )
and exists ( select 1 from v_user_data_perm b where a.group_id=b.group_id and a.hos_id=b.hos_id and to_char(a.proc_store_id) = b.perm_code and user_id=#{user_id} AND TABLE_CODE = upper('hos_store_dict') and b.is_write = 1
union all
select 1 from v_user_data_perm b where a.group_id=b.group_id and a.hos_id=b.hos_id and to_char(a.proc_store_id) = b.perm_code and user_id=#{user_id} AND TABLE_CODE = upper('hos_store_dict') and b.is_read = 1 )
AND a.ass_id = to_number(#{ass_id})
AND a.file_number = #{file_number}
AND a.contract_code = #{contract_code}
AND a.price >= #{price_beg}
AND a.price <= #{price_end}
AND a.ass_no = to_number(#{ass_no})
AND atd.ass_type_id = to_number(#{ass_type_id})
AND atd.acc_type_code = #{fina_type_code}
