AND a.is_measure = to_number(#{is_measure})
AND a.is_throw = #{is_throw}
AND a.pact_code = #{pact_code}
AND a.ins_money = #{ins_money}
AND a.ins_date = #{ins_date}
AND a.accept_emp = #{accept_emp}
AND a.accept_date = #{accept_date}
AND a.service_date = #{service_date}
AND a.ass_seq_no = #{ass_seq_no}
AND a.location like '%${location}%'
AND a.note like '%${note}%'
AND a.ass_in_no = #{ass_in_no}
AND a.in_date >= to_date(#{in_date_beg},'YYYY-MM-DD')
AND a.in_date <= to_date(#{in_date_end},'YYYY-MM-DD')
AND a.run_date >= to_date(#{run_date_beg},'YYYY-MM-DD')
AND a.run_date <= to_date(#{run_date_end},'YYYY-MM-DD')
AND a.run_date = #{run_date}
AND a.is_bar = #{is_bar}
AND a.bar_type = #{bar_type}
AND a.bar_code like '%${bar_code}%'
AND a.bar_url = #{bar_url}
AND a.book_amount = #{book_amount}
AND a.relic_amount = #{relic_amount}
AND a.relic_grade_code = #{relic_grade_code}
AND a.is_init = #{is_init}
AND a.dispose_type = to_number(#{dispose_type})
AND a.dispose_cost = #{dispose_cost}
AND a.dispose_income = #{dispose_income}
AND a.dispose_tax = #{dispose_tax}
AND a.proj_id = to_number(#{proj_id})
AND a.ass_purpose = #{ass_purpose}
AND ( upper(a.ass_card_no) like upper('%${ass_card_no}%') or ad.ass_name like '%${ass_card_no}%' or ad.spell_code like '%${ass_card_no}%' )
AND a.ass_card_no like 'ZCKPDZ%'
AND a.ass_card_no not like 'ZCKPDZ%'
AND (a.ass_ori_card_no like upper('%${ass_ori_card_no}%') or a.ass_ori_card_no like lower('%${ass_ori_card_no}%'))
AND a.is_bar_print = #{is_bar_print}
AND a.simple_name like '%${simple_name}%'
AND a.proc_store_id = to_number(#{proc_store_id})
AND a.common_name like '%${common_name}%'
AND a.six_type_code = #{six_type_code}
union all
SELECT null group_id, null hos_id, null copy_code, '' ass_card_no, null ass_ori_card_no, null ass_id, null ass_no, null ass_id_code, null ass_id_name, null gb_code, null gb_code_name, null ass_spec, null ass_mondl, null ass_brand, null ass_amount, null unit_code, null unit_code_name, null fac_no, null fac_id, null fac_id_code, null fac_id_name, null ven_id, null ven_no, null ven_id_code, null ven_id_name, null is_dept, null is_dept_name, null dept_id, null dept_no, null dept_id_code, null dept_id_name, null store_id, null store_no, null store_id_code, null store_id_name, sum(a.price) price, sum(a.depre_money) depre_money, sum(a.manage_depre_money) manage_depre_money, sum(a.cur_money) cur_money, sum(a.fore_money) fore_money, null buy_type, null buy_type_name, null use_state, null use_state_name, null is_depr, null is_depr_name, null depr_method, null depr_method_name, null acc_depre_amount, null is_manage_depre, null is_manage_depre_name, null manage_depr_method, null manage_depr_method_name, null manage_depre_amount, null is_measure, null is_measure_name, null is_throw, null is_throw_name, null pact_code, null contract_code, null file_number, sum(a.ins_money) ins_money, null ins_date, null accept_emp, null accept_emp_name, null accept_date, null service_date, null ass_seq_no, null location, null note, null ass_in_no, null in_date, null run_date, null is_bar, null is_bar_name, null bar_type, null bar_type_name, null bar_code, null bar_url, null book_amount, null relic_amount, null relic_grade_code, null is_init, null is_init_name, null dispose_type, null dispose_type_name, sum(a.dispose_cost) dispose_cost, sum(a.dispose_income) dispose_income, sum(a.dispose_tax) dispose_tax, null proj_id, null proj_no, null proj_id_code, null proj_id_name, null ass_purpose, null ass_purpose_name, null dispose_date, null apply_date, null add_depre_month, null add_manage_month, null reg_no, null reg_no_name, null is_bar_print, null is_bar_print_name, null measure_type, null measure_typ