mapper.insert:
INSERT INTO ASS_CHECK_DETAIL_House ( group_id , hos_id , copy_code , check_plan_no , check_no , ass_card_no , ass_id , ass_no , acc_amount , check_amount , pl_amount , pl_reason
) select t.* from(
select #{item.group_id,jdbcType=INTEGER} , #{item.hos_id,jdbcType=INTEGER} , #{item.copy_code,jdbcType=VARCHAR} , #{item.check_plan_no,jdbcType=VARCHAR} , #{item.check_no,jdbcType=VARCHAR} , #{item.ass_card_no,jdbcType=VARCHAR} , #{item.ass_id,jdbcType=INTEGER} , #{item.ass_no,jdbcType=INTEGER} , #{item.acc_amount,jdbcType=INTEGER} , #{item.check_amount,jdbcType=INTEGER} , #{item.pl_amount,jdbcType=INTEGER} , #{item.pl_reason,jdbcType=VARCHAR}
from dual
)t

