mapper.select:
select t1.group_id, t1.hos_id, t1.copy_code, t1.check_plan_no, t1.check_no , t1.ass_card_no, t1.ass_id, t1.ass_no, acc_amount, nvl(check_amount,0) check_amount, pl_amount, pl_reason, ass_name, ass_type_name, t5.ass_ori_card_no,'' bar_code, case when t6.state = 0 then '新建' when t6.state = 1 then '审核' else '' end state_name from ASS_CHECK_DETAIL_House t1 left join ass_no_dict t2 on t1.group_id = t2.group_id and t1.hos_id = t2.hos_id and t1.copy_code = t2.copy_code and t1.ass_id = t2.ass_id and t1.ass_no = t2.ass_no and t2.is_stop = 0 left join ass_type_dict t3 on t3.group_id = t2.group_id and t3.hos_id = t2.hos_id and t3.copy_code = t2.copy_code and t3.ass_type_id = t2.ass_type_id and t3.is_last = 1 and t3.is_stop = 0 left join ass_card_House t5 on t1.group_id = t5.group_id and t1.hos_id = t5.hos_id and t1.copy_code = t5.copy_code and t1.ass_card_no = t5.ass_card_no left join ASS_CHECK_HOUSE t6 on t1.check_no=t6.check_no and t1.check_plan_no=t6.check_plan_no where t1.group_id = #{group_id,jdbcType=INTEGER} AND t1.hos_id = #{hos_id,jdbcType=INTEGER} AND t1.copy_code = #{copy_code,jdbcType=VARCHAR} AND t1.check_plan_no = #{check_plan_no,jdbcType=VARCHAR}
AND t1.check_no = #{check_no,jdbcType=VARCHAR}
AND t1.ass_card_no = #{ass_card_no,jdbcType=VARCHAR}
AND t5.ass_ori_card_no = #{ass_ori_card_no,jdbcType=VARCHAR}
AND t5.bar_code = #{bar_code,jdbcType=VARCHAR}
and ( t2.ass_code like '%${ass_id}%' or t2.ass_name like '%${ass_id}%' or t2.spell_code like '%${ass_id}%' or t2.wbx_code like '%${ass_id}%' )
AND t1.ass_no = #{ass_no,jdbcType=INTEGER}
AND t3.ass_type_id = #{ass_type_id,jdbcType=INTEGER}
order by t1.ass_card_no asc

SELECT group_id, hos_id, copy_code, check_plan_no, check_no, ass_card_no, ass_id, ass_no, acc_amount, check_amount, pl_amount, pl_reason FROM ASS_CHECK_DETAIL_House WHERE group_id = #{group_id,jdbcType=INTEGER} and hos_id = #{hos_id,jdbcType=INTEGER} and copy_code = #{copy_code,jdbcType=VARCHAR} and check_plan_no = #{check_plan_no,jdbcType=VARCHAR} and check_no = #{check_no,jdbcType=VARCHAR} and ass_card_no = #{ass_card_no,jdbcType=VARCHAR}
AND ass_id = #{ass_id,jdbcType=INTEGER}
AND ass_no = #{ass_no,jdbcType=INTEGER}
AND acc_amount = #{acc_amount,jdbcType=INTEGER}
AND check_amount = #{check_amount,jdbcType=INTEGER}
AND pl_amount = #{pl_amount,jdbcType=INTEGER}
AND pl_reason = #{pl_reason,jdbcType=VARCHAR}
order by ass_card_no asc

SELECT group_id, hos_id, copy_code, check_plan_no, check_no, ass_card_no, ass_id, ass_no, acc_amount, check_amount, pl_amount, pl_reason FROM ASS_CHECK_DETAIL_House WHERE group_id = #{group_id,jdbcType=INTEGER} and hos_id = #{hos_id,jdbcType=INTEGER} and copy_code = #{copy_code,jdbcType=VARCHAR} and check_plan_no = #{check_plan_no,jdbcType=VARCHAR} and check_no = #{check_no,jdbcType=VARCHAR} and ass_card_no = #{ass_card_no,jdbcType=VARCHAR}
AND ass_id = #{ass_id,jdbcType=INTEGER}
AND ass_no = #{ass_no,jdbcType=INTEGER}
AND acc_amount = #{acc_amount,jdbcType=INTEGER}
AND check_amount = #{check_amount,jdbcType=INTEGER}
AND pl_amount = #{pl_amount,jdbcType=INTEGER}
AND pl_reason = #{pl_reason,jdbcType=VARCHAR}
order by ass_card_no asc

mapper.update:
BEGIN
UPDATE ASS_CHECK_DETAIL_House SET check_amount=acc_amount  
WHERE
	group_id = #{group_id,jdbcType=INTEGER} and
	hos_id = #{hos_id,jdbcType=INTEGER} and
	copy_code = #{copy_code,jdbcType=VARCHAR} and
	check_plan_no = #{check_plan_no,jdbcType=VARCHAR} and
	check_no = #{check_no,jdbcType=VARCHAR} 

	and ass_card_no = #{ass_card_no,jdbcType=VARCHAR}


	and	 ass_card_no in ${ass_card_no_all}

	;
UPDATE ASS_CHECK_DETAIL_House SET pl_amount= acc_amount - check_amount
WHERE
	group_id = #{group_id,jdbcType=INTEGER} and
	hos_id = #{hos_id,jdbcType=INTEGER} and
	copy_code = #{copy_code,jdbcType=VARCHAR} and
	check_plan_no = #{check_plan_no,jdbcType=VARCHAR} and
	check_no = #{check_no,jdbcType=VARCHAR}

	and ass_card_no = #{ass_card_no,jdbcType=VARCHAR}


	and	 ass_card_no in ${ass_card_no_all}

	;
	END;

mapper.select:
  select t1.check_no,t1.check_plan_no,t1.check_date,t1.summary,
  case when t1.state = 0 then '新建' when t1.state = 1 then '审核' else '' end state,t1.note,t2.user_name  from  ASS_CHECK_HOUSE  t1
 left join sys_user t2 on t1.group_id = t2.group_id
  and t1.hos_id = t2.hos_id
 and t1.check_emp = t2.user_id 
    	where                     
			t1.group_id = #{group_id}
			AND t1.hos_id = #{hos_id}
			AND t1.copy_code = #{copy_code}
			AND t1.check_no = #{check_no}

	select t1.ass_card_no,t1.check_amount,t1.ass_id,t1.ass_no,t1.pl_amount,t1.pl_reason,t1.acc_amount,t2.ass_name,t2.ass_code
  from  ASS_CHECK_DETAIL_HOUSE  t1
 left join ass_dict t2 on t1.group_id = t2.group_id
  and t1.hos_id = t2.hos_id
 and t1.ass_id=t2.ass_id
	where                     
			t1.group_id = #{group_id}
			AND t1.hos_id = #{hos_id}
			AND t1.copy_code = #{copy_code}
			AND t1.check_no in  (#{check_no,jdbcType=VARCHAR})

SELECT check_no  FROM ASS_CHECK_HOUSE 
WHERE
	group_id = #{group_id,jdbcType=INTEGER}
	AND hos_id = #{hos_id,jdbcType=INTEGER}
	AND copy_code = #{copy_code,jdbcType=VARCHAR}

	AND check_no in  (#{check_no,jdbcType=VARCHAR})



	and state != #{state,jdbcType=INTEGER}
