mapper.insert:
INSERT INTO ASS_CHECK_House ( group_id , hos_id , copy_code , check_plan_no , check_no , summary , check_emp , check_date , note , state
) select ASS_CHECK_House_SEQ.nextval , t.* from(
select #{item.group_id,jdbcType=INTEGER} , #{item.hos_id,jdbcType=INTEGER} , #{item.copy_code,jdbcType=VARCHAR} , #{item.check_plan_no,jdbcType=VARCHAR} , #{item.check_no,jdbcType=VARCHAR} , #{item.summary,jdbcType=VARCHAR} , #{item.check_emp,jdbcType=INTEGER} , #{item.check_date,jdbcType=DATE} , #{item.note,jdbcType=VARCHAR} , #{item.state,jdbcType=INTEGER}
from dual
)t

mapper.update:

UPDATE ASS_CHECK_House 


summary = #{summary,jdbcType=VARCHAR}
,


