mapper.select:

SELECT 
group_id,
hos_id,
copy_code,
check_plan_no,
check_no,
summary,
check_emp,
check_date,
note,
state
FROM ASS_CHECK_House 
where               
 group_id = #{group_id,jdbcType=INTEGER}
AND hos_id = #{hos_id,jdbcType=INTEGER}
AND copy_code = #{copy_code,jdbcType=VARCHAR}
AND check_plan_no = #{check_plan_no,jdbcType=VARCHAR}

AND check_no = #{check_no,jdbcType=VARCHAR}


AND summary = #{summary,jdbcType=VARCHAR}


AND check_emp = #{check_emp,jdbcType=INTEGER}


AND check_date = #{check_date,jdbcType=DATE}


AND note = #{note,jdbcType=VARCHAR}


AND state = #{state,jdbcType=INTEGER}

order by check_no asc

SELECT group_id, hos_id, copy_code, check_plan_no, check_no, summary, check_emp, check_date, note, state FROM ASS_CHECK_House WHERE group_id = #{group_id,jdbcType=INTEGER} and hos_id = #{hos_id,jdbcType=INTEGER} and copy_code = #{copy_code,jdbcType=VARCHAR} and check_plan_no = #{check_plan_no,jdbcType=VARCHAR} and check_no = #{check_no,jdbcType=VARCHAR}
AND summary = #{summary,jdbcType=VARCHAR}
AND check_emp = #{check_emp,jdbcType=INTEGER}
AND check_date = #{check_date,jdbcType=DATE}
AND note = #{note,jdbcType=VARCHAR}
AND state = #{state,jdbcType=INTEGER}
order by check_no asc

