		AND ass_no = #{item.ass_no,jdbcType=INTEGER}

mapper.select:
SELECT a.group_id, a.hos_id, a.copy_code, a.check_ap_no, a.ass_id||'@'||a.ass_no ass_id, a.ass_no, c.ass_code, c.ass_name, c.ass_spec, c.ass_model, c.ass_brand, a.acc_amount, a.price, a.add_depre, a.add_depre_month, a.cur_money, a.fore_money, a.p_reason, d.fac_id, d.fac_name FROM ASS_CHECK_AP_DETAIL_LAND a left join ass_no_dict c on a.group_id = c.group_id and a.hos_id = c.hos_id and a.copy_code = c.copy_code and a.ass_id = c.ass_id and a.ass_no = c.ass_no and c.is_stop = 0 left join hos_fac_dict d on a.group_id = d.group_id and a.hos_id = d.hos_id and c.fac_id = d.fac_id and d.is_stop = 0
a.group_id = #{group_id,jdbcType=INTEGER} AND a.hos_id = #{hos_id,jdbcType=INTEGER} AND a.copy_code = #{copy_code,jdbcType=VARCHAR}
AND a.check_ap_no = #{check_ap_no,jdbcType=VARCHAR}
AND a.ass_id = #{ass_id,jdbcType=INTEGER}
AND a.ass_no = #{ass_no,jdbcType=INTEGER}
AND a.acc_amount = #{acc_amount,jdbcType=INTEGER}
AND a.price = #{price,jdbcType=INTEGER}
AND a.add_depre = #{add_depre,jdbcType=INTEGER}
AND a.add_depre_month = #{add_depre_month,jdbcType=INTEGER}
AND a.cur_money = #{cur_money,jdbcType=INTEGER}
AND a.fore_money = #{fore_money,jdbcType=INTEGER}
AND a.p_reason = #{p_reason,jdbcType=VARCHAR}
order by a.group_id asc

SELECT a.group_id, a.hos_id, a.copy_code, a.check_ap_no, a.ass_id||'@'||a.ass_no ass_id, a.ass_no, c.ass_code, c.ass_name, c.ass_spec, c.ass_model, c.ass_brand, a.acc_amount, a.price, a.add_depre, a.add_depre_month, a.cur_money, a.fore_money, a.p_reason, d.fac_id, d.fac_name FROM ASS_CHECK_AP_DETAIL_LAND a left join ass_no_dict c on a.group_id = c.group_id and a.hos_id = c.hos_id and a.copy_code = c.copy_code and a.ass_id = c.ass_id and a.ass_no = c.ass_no and c.is_stop = 0 left join hos_fac_dict d on a.group_id = d.group_id and a.hos_id = d.hos_id and c.fac_id = d.fac_id and d.is_stop = 0
a.group_id = #{group_id,jdbcType=INTEGER} AND a.hos_id = #{hos_id,jdbcType=INTEGER} AND a.copy_code = #{copy_code,jdbcType=VARCHAR} AND a.check_ap_no = #{check_ap_no,jdbcType=VARCHAR}
AND a.ass_id = #{ass_id,jdbcType=INTEGER}
AND a.ass_no = #{ass_no,jdbcType=INTEGER}


SELECT 
	group_id,
	hos_id,
	copy_code,
	check_ap_no,
	ass_id,
	ass_no,
	acc_amount,
	price,
	add_depre,
	add_depre_month,
	cur_money,
	fore_money,
	p_reason
FROM ASS_CHECK_AP_DETAIL_LAND 

	group_id = #{group_id,jdbcType=INTEGER}
	AND hos_id = #{hos_id,jdbcType=INTEGER}
	AND copy_code = #{copy_code,jdbcType=VARCHAR}

	AND check_ap_no = #{check_ap_no,jdbcType=VARCHAR}


	AND ass_id = #{ass_id,jdbcType=INTEGER}


	AND ass_no = #{ass_no,jdbcType=INTEGER}


	AND acc_amount = #{acc_amount,jdbcType=INTEGER}


	AND price = #{price,jdbcType=INTEGER}


	AND add_depre = #{add_depre,jdbcType=INTEGER}


	AND add_depre_month = #{add_depre_month,jdbcType=INTEGER}


	AND cur_money = #{cur_money,jdbcType=INTEGER}


	AND fore_money = #{fore_money,jdbcType=INTEGER}


	AND p_reason = #{p_reason,jdbcType=VARCHAR}


order by group_id asc


