mapper.insert:
INSERT INTO ASS_DEPRE_DETAIL_General ( group_id , hos_id , copy_code , change_no , ass_card_no , old_depre , change_depre , new_depre , note
) select t.* from(
select #{item.group_id} , #{item.hos_id} , #{item.copy_code} , #{item.change_no} , #{item.ass_card_no} , nvl(#{item.old_depre},0) , nvl(#{item.change_depre},0) , nvl(#{item.new_depre},0) , nvl(#{item.note},'')
from dual
)t

mapper.update:

UPDATE ass_depre_detail_General  set
	change_depre = nvl(#{change_depre},0)
	,
	new_depre =  nvl(#{new_depre},0)

AND group_id = #{group_id}
AND hos_id = #{hos_id}
AND copy_code = #{copy_code}
AND change_no = #{change_no}
AND ass_card_no = #{ass_card_no}

update.foreach:
UPDATE ass_depre_detail_General set
change_depre = nvl(#{item.change_depre},0)
,
new_depre = nvl(#{item.new_depre},0)

group_id = #{item.group_id}
AND hos_id = #{item.hos_id}
AND copy_code = #{item.copy_code}
AND change_no = #{item.change_no}
AND ass_card_no = #{item.ass_card_no}

mapper.update:

