		AND ass_card_no = #{item.ass_card_no}

mapper.select:
SELECT a.group_id, a.hos_id, a.copy_code, a.change_no, a.ass_card_no, c.ass_id, c.ass_no, c.ass_code, c.ass_name, a.old_depre, a.change_depre, a.new_depre, a.note FROM ASS_DEPRE_DETAIL_General a left join ass_card_General b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.copy_code = b.copy_code and a.ass_card_no = b.ass_card_no
left join ass_no_dict c on b.group_id = c.group_id and b.hos_id = c.hos_id and b.copy_code = c.copy_code and b.ass_id = c.ass_id and c.is_stop = 0
AND a.group_id = #{group_id}
AND a.hos_id = #{hos_id}
AND a.copy_code = #{copy_code}
AND a.change_no = #{change_no}
AND a.ass_card_no = #{ass_card_no}
order by a.ass_card_no asc

SELECT a.group_id, a.hos_id, a.copy_code, a.change_no, a.ass_card_no, c.ass_id, c.ass_no, c.ass_code, c.ass_name, a.old_depre, a.change_depre, a.new_depre, a.note FROM ASS_DEPRE_DETAIL_General a left join ass_card_General b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.copy_code = b.copy_code and a.ass_card_no = b.ass_card_no
left join ass_no_dict c on b.group_id = c.group_id and b.hos_id = c.hos_id and b.copy_code = c.copy_code and b.ass_id = c.ass_id and c.is_stop = 0
AND a.group_id = #{group_id} AND a.hos_id = #{hos_id} AND a.copy_code = #{copy_code} AND a.change_no = #{change_no}

SELECT a.group_id, a.hos_id, a.copy_code, a.change_no, a.ass_card_no, c.ass_id, c.ass_no, c.ass_code, c.ass_name, a.old_depre, a.change_depre, a.new_depre, a.note FROM ASS_DEPRE_DETAIL_General a left join ass_card_General b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.copy_code = b.copy_code and a.ass_card_no = b.ass_card_no
left join ass_no_dict c on b.group_id = c.group_id and b.hos_id = c.hos_id and b.copy_code = c.copy_code and b.ass_id = c.ass_id and c.is_stop = 0
AND a.group_id = #{group_id} AND a.hos_id = #{hos_id} AND a.copy_code = #{copy_code} AND a.change_no = #{change_no}


SELECT 
group_id,
hos_id,
copy_code,
change_no,
ass_card_no,
old_depre,
change_depre,
new_depre,
note
FROM ASS_DEPRE_DETAIL_General 


AND group_id = #{group_id}


AND hos_id = #{hos_id}


AND copy_code = #{copy_code}


AND change_no = #{change_no}


AND ass_card_no = #{ass_card_no}


AND old_depre = #{old_depre}


AND change_depre = #{change_depre}


AND new_depre = #{new_depre}


AND note = #{note}


order by group_id asc


