mapper.insert:
INSERT INTO ASS_IN_DETAIL_LAND ( group_id , hos_id , copy_code , ass_in_no , ass_id , ass_no , in_amount , price , note , reg_no ) select t.* from(
select #{item.group_id,jdbcType=INTEGER} , #{item.hos_id,jdbcType=INTEGER} , #{item.copy_code,jdbcType=VARCHAR} , #{item.ass_in_no,jdbcType=VARCHAR} , #{item.ass_id,jdbcType=INTEGER} , #{item.ass_no,jdbcType=INTEGER} , #{item.in_amount,jdbcType=INTEGER} , #{item.price,jdbcType=INTEGER} , #{item.note,jdbcType=VARCHAR} , #{item.reg_no,jdbcType=VARCHAR} from dual
)t

